import { cn } from "../../lib/utils";
import React from "react";
import { motion } from "framer-motion";
 
export const BackgroundGradient = ({
  children,
  className,
  containerClassName,
  animate = true,
  gradientColors = {
    start: "#3b82f6",
    middle: "#60a5fa",
    end: "#93c5fd",
    base: "#1d4ed8"
  }
}) => {
  const variants = {
    initial: {
      backgroundPosition: "0 50%",
    },
    animate: {
      backgroundPosition: ["0, 50%", "100% 50%", "0 50%"],
    },
  };

  const gradientStyle = `radial-gradient(circle farthest-side at 0 100%,${gradientColors.start},transparent),
                        radial-gradient(circle farthest-side at 100% 0,${gradientColors.middle},transparent),
                        radial-gradient(circle farthest-side at 100% 100%,${gradientColors.end},transparent),
                        radial-gradient(circle farthest-side at 0 0,${gradientColors.base},#141316)`;

  return (
    <div className={cn("relative p-[2px] group", containerClassName)}>
      {/* Animated border container */}
      <div className="absolute inset-0 rounded-xl overflow-hidden">
        {/* Rotating gradient border */}
        <motion.div
          animate={{
            rotate: [0, 360],
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            ease: "linear"
          }}
          className="absolute -inset-[100%] bg-[length:50%_50%] opacity-50"
          style={{
            background: gradientStyle,
            backgroundRepeat: 'no-repeat',
          }}
        />
      </div>

      {/* Static outer glow */}
      <div
        className={cn(
          "absolute inset-0 rounded-xl z-[1] opacity-40 group-hover:opacity-75 blur-xl transition duration-500"
        )}
        style={{
          background: gradientStyle
        }}
      />

      {/* Static border gradient */}
      <div
        className={cn(
          "absolute inset-0 rounded-xl z-[1]"
        )}
        style={{
          background: gradientStyle
        }}
      />

      {/* Moving highlight effect */}
      <motion.div
        animate={{
          x: ["0%", "100%"],
          opacity: [0, 1, 0]
        }}
        transition={{
          duration: 3,
          repeat: Infinity,
          repeatType: "loop",
          ease: "easeInOut"
        }}
        className="absolute inset-0 rounded-xl z-[2]"
      >
        <div className="w-1/3 h-full bg-gradient-to-r from-transparent via-white/10 to-transparent transform -skew-x-12" />
      </motion.div>

      {/* 3D effect layers */}
      <div className="absolute inset-[1px] rounded-[11px] bg-gradient-to-b from-white/5 to-transparent z-[2]" />
      <div className="absolute inset-[1px] rounded-[11px] bg-gradient-to-t from-black/20 to-transparent z-[2]" />
      
      {/* Content container with inner shadow */}
      <div className={cn(
        "relative z-10 rounded-[11px] bg-gradient-to-b from-black/90 to-black/80",
        "shadow-[inset_0_1px_1px_rgba(255,255,255,0.1),inset_0_-1px_1px_rgba(0,0,0,0.8)]",
        className
      )}>
        {children}
      </div>

      {/* Corner accents */}
      <div className="absolute top-0 left-0 w-4 h-4 border-t-2 border-l-2 rounded-tl-xl border-white/20 z-[3]" />
      <div className="absolute top-0 right-0 w-4 h-4 border-t-2 border-r-2 rounded-tr-xl border-white/20 z-[3]" />
      <div className="absolute bottom-0 left-0 w-4 h-4 border-b-2 border-l-2 rounded-bl-xl border-white/20 z-[3]" />
      <div className="absolute bottom-0 right-0 w-4 h-4 border-b-2 border-r-2 rounded-br-xl border-white/20 z-[3]" />
    </div>
  );
};
