import React from 'react';
import { WavyBackground } from '../../ui/wavy-background';

const ScoreboardRow = ({ data, isActive }) => {
  return (
    <div className="relative w-full h-24">
      <WavyBackground 
        colors={['#1a237e', '#0d47a1', '#1565c0', '#1976d2', '#1e88e5']}
        blur={2}
        speed="slow"
        waveOpacity={0.2}
        containerClassName="!h-full !w-full absolute inset-0"
        className="!h-full !w-full"
        backgroundFill="rgba(0,0,0,0)"
      >
        <div className="relative z-10 flex items-center justify-between p-4 h-full">
          <div className="flex items-center space-x-4">
            {data.teamLogo && (
              <img 
                src={data.teamLogo} 
                alt={data.teamName} 
                className="w-8 h-8 object-contain"
              />
            )}
            <span className="text-lg font-semibold text-white">
              {data.teamName}
            </span>
          </div>
          <div className="flex items-center space-x-4">
            <span className="text-lg font-bold text-white">
              {data.score}
            </span>
            {data.overs && (
              <span className="text-sm text-gray-300">
                ({data.overs})
              </span>
            )}
          </div>
        </div>
      </WavyBackground>
    </div>
  );
};

export default ScoreboardRow;
