import React from 'react';

export const AnimatedDotsBackground = ({
  dotColor = 'rgba(59, 130, 246, 0.2)',
  dotSize = 1,
  gap = 20,
  className = '',
  animated = true,
  direction = 'diagonal',
}) => {
  const getAnimation = () => {
    switch (direction) {
      case 'horizontal':
        return 'moveDotsHorizontal';
      case 'vertical':
        return 'moveDotsVertical';
      default:
        return 'moveDotsDiagonal';
    }
  };

  return (
    <div className={`absolute inset-0 pointer-events-none ${className}`}>
      <div
        className="absolute inset-0 transition-opacity duration-500"
        style={{
          backgroundImage: `radial-gradient(${dotColor} ${dotSize}px, transparent ${dotSize + 0.5}px)`,
          backgroundSize: `${gap}px ${gap}px`,
          animation: animated ? `${getAnimation()} 30s linear infinite` : 'none',
        }}
      />
      <style jsx>{`
        @keyframes moveDotsDiagonal {
          0% {
            background-position: 0 0;
          }
          100% {
            background-position: ${gap * 2}px ${gap * 2}px;
          }
        }
        @keyframes moveDotsHorizontal {
          0% {
            background-position: 0 0;
          }
          100% {
            background-position: ${gap * 2}px 0;
          }
        }
        @keyframes moveDotsVertical {
          0% {
            background-position: 0 0;
          }
          100% {
            background-position: 0 ${gap * 2}px;
          }
        }
      `}</style>
    </div>
  );
};
