import React from 'react';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';
import { AnimatedDotsBackground } from '../../ui/animated-dots-background';

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  return (
    <div className="flex flex-col gap-4">
      {/* Main Score Row */}
      <div className="w-full flex items-center justify-between px-8 py-4
                    animated-content-box premium-box-shadow content-box-glow
                    rounded-lg border border-blue-400/20 relative
                    overflow-hidden group hover:bg-slate-800/30 transition-all duration-500">
        {/* Animated Dotted Background */}
        <AnimatedDotsBackground 
          dotColor="rgba(59, 130, 246, 0.3)"
          dotSize={1.5}
          gap={24}
          className="opacity-0 group-hover:opacity-100 transition-all duration-700"
          direction="diagonal"
        />

        {/* Animated Gradient Border */}
        <div className="absolute inset-0 bg-gradient-to-r from-blue-500/5 via-purple-500/5 to-blue-500/5 
                        mix-blend-overlay group-hover:opacity-100 transition-all duration-500" />

        {/* Content */}
        <TeamScoreBox 
          teamName={team1.name}
          score={team1.score}
          overs={team1.overs}
          logo={team1.logo}
        />

        <EventBox event={currentEvent} />

        <TeamScoreBox 
          teamName={team2.name}
          score={team2.score}
          overs={team2.overs}
          logo={team2.logo}
          isReversed={true}
        />
      </div>

      {/* Additional Info Box - Centered below */}
      {additionalInfo && (
        <div className="flex justify-center relative group">
          <DottedBackground 
            dotColor="rgba(59, 130, 246, 0.1)"
            dotSize={0.8}
            gap={16}
            className="opacity-0 group-hover:opacity-100 transition-opacity duration-700"
          />
          <AdditionalInfoBox additionalInfo={additionalInfo} />
        </div>
      )}
    </div>
  );
};

export default ScoreboardRow;
