import React from 'react';
import { BackgroundGradientAnimation } from '../../ui/background-gradient-animation';

const ScoreboardRow = ({ children, className }) => {
  return (
    <BackgroundGradientAnimation
      gradientBackgroundStart="rgb(10, 24, 55)"    // Darker, richer blue
      gradientBackgroundEnd="rgb(17, 24, 39)"      // Deep navy
      firstColor="59, 130, 246"                    // Bright blue
      secondColor="147, 197, 253"                  // Light blue
      thirdColor="37, 99, 235"                     // Royal blue
      fourthColor="29, 78, 216"                    // Medium blue
      fifthColor="96, 165, 250"                    // Sky blue
      pointerColor="147, 197, 253"                 // Bright interactive color
      size="100%"
      blendingValue="overlay"                      // Changed to overlay for more vibrancy
      className={`${className} overflow-hidden`}
      containerClassName="w-full relative"
      interactive={true}
    >
      <div className="relative z-10">
        <div className="p-4">
          <div className="w-full flex items-center justify-between px-8 py-4
                      animated-content-box premium-box-shadow
                      rounded-lg border border-blue-400/30
                      bg-gradient-to-br from-[rgba(15,23,42,0.9)] to-[rgba(15,23,42,0.95)]
                      backdrop-blur-[2px]">
            {children}
          </div>
        </div>
      </div>

      {/* Additional gradient overlay for more depth */}
      <div className="absolute inset-0 bg-gradient-to-br from-blue-500/10 via-transparent to-blue-600/10 
                    mix-blend-overlay pointer-events-none" />
    </BackgroundGradientAnimation>
  );
};

export default ScoreboardRow;
