import { cn } from "../../lib/utils";
import React from "react";

export const Meteors = ({
  number,
  className,
}) => {
  const meteors = new Array(number || 20).fill(true);
  return (
    <div className="absolute inset-0 z-10">
      {meteors.map((el, idx) => (
        <span
          key={"meteor" + idx}
          className={cn(
            "fixed h-0.5 w-0.5 rounded-full bg-blue-500",
            "animate-meteor-effect pointer-events-none",
            "before:fixed before:w-[50px] before:h-[1px]",
            "before:bg-gradient-to-r before:from-blue-500 before:to-transparent",
            "before:animate-meteor-effect before:pointer-events-none",
            className
          )}
          style={{
            top: Math.floor(Math.random() * 100) + "%",
            left: Math.floor(Math.random() * 100) + "%",
            animationDelay: Math.floor(Math.random() * 8) + "s",
          }}
        />
      ))}
    </div>
  );
};
