import React from 'react';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';
import { motion, AnimatePresence } from 'framer-motion';

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  return (
    <div className="flex flex-col gap-4">
      {/* Main Score Row */}
      <motion.div 
        className="w-full flex items-center justify-between px-8 py-4
                  animated-content-box premium-box-shadow content-box-glow
                  rounded-lg border border-blue-400/20
                  relative overflow-hidden"
        initial={{ opacity: 0.9 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 2, repeat: Infinity }}
      >
        {/* Pulsing glow effect overlay */}
        <div className="absolute inset-0 pointer-events-none">
          <div className="absolute inset-0 animate-score-pulse-slow 
                        bg-gradient-to-r from-blue-500/0 via-blue-500/10 to-blue-500/0" />
          <div className="absolute inset-0 animate-score-pulse-fast
                        bg-gradient-to-r from-blue-400/0 via-blue-400/5 to-blue-400/0" />
        </div>

        {/* Border glow effect */}
        <div className="absolute inset-0 -z-10 animate-border-glow">
          <div className="absolute inset-[1px] rounded-lg 
                       bg-gradient-to-r from-blue-500/20 via-blue-500/40 to-blue-500/20" />
        </div>

        {/* Left Team */}
        <TeamScoreBox 
          teamName={team1.name}
          score={team1.score}
          overs={team1.overs}
          logo={team1.logo}
        />

        {/* Middle Event */}
        <EventBox event={currentEvent} />

        {/* Right Team */}
        <TeamScoreBox 
          teamName={team2.name}
          score={team2.score}
          overs={team2.overs}
          logo={team2.logo}
          isReversed={true}
        />
      </motion.div>

      {/* Additional Info Box - Centered below */}
      <AnimatePresence>
        {additionalInfo && (
          <motion.div 
            className="flex justify-center"
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 10 }}
            transition={{ duration: 0.3 }}
          >
            <AdditionalInfoBox additionalInfo={additionalInfo} />
          </motion.div>
        )}
      </AnimatePresence>

      {/* Add these styles */}
      <style jsx>{`
        @keyframes scorePulseSlow {
          0%, 100% {
            opacity: 0;
            transform: translateX(-100%);
          }
          50% {
            opacity: 1;
            transform: translateX(100%);
          }
        }

        @keyframes scorePulseFast {
          0%, 100% {
            opacity: 0;
            transform: translateX(100%);
          }
          50% {
            opacity: 1;
            transform: translateX(-100%);
          }
        }

        @keyframes borderGlow {
          0%, 100% {
            opacity: 0.3;
          }
          50% {
            opacity: 0.7;
          }
        }

        .animate-score-pulse-slow {
          animation: scorePulseSlow 8s ease-in-out infinite;
        }

        .animate-score-pulse-fast {
          animation: scorePulseFast 5s ease-in-out infinite;
        }

        .animate-border-glow {
          animation: borderGlow 4s ease-in-out infinite;
        }

        .content-box-glow {
          box-shadow: 
            0 0 20px rgba(59, 130, 246, 0.2),
            inset 0 0 20px rgba(59, 130, 246, 0.1);
          transition: all 0.3s ease-out;
        }

        .content-box-glow:hover {
          box-shadow: 
            0 0 30px rgba(59, 130, 246, 0.3),
            inset 0 0 30px rgba(59, 130, 246, 0.15);
          transform: translateY(-1px);
        }
      `}</style>
    </div>
  );
};

export default ScoreboardRow;
