import React from 'react';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';
import { WavyBackground } from '../../../components/ui/wavy-background';

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  return (
    <div className="flex flex-col gap-4 min-h-[120px]">
      <WavyBackground 
        colors={[
          "rgba(24, 113, 255, 0.4)",   // Blue
          "rgba(100, 0, 200, 0.3)",    // Purple
          "rgba(0, 40, 100, 0.4)",     // Dark Blue
          "rgba(20, 0, 60, 0.3)",      // Deep Purple
          "rgba(0, 20, 80, 0.4)"       // Navy
        ]}
        waveWidth={30}
        backgroundFill="rgb(13, 22, 49)"
        blur={5}
        speed="slow"
        waveOpacity={0.3}
        containerClassName="rounded-lg relative overflow-hidden h-[120px]"
        className="w-full h-full"
      >
        {/* Main Score Row */}
        <div className="w-full flex items-center justify-between px-8 py-4
                     rounded-lg border border-blue-400/20 relative
                     bg-gradient-to-br from-black/30 to-black/10 backdrop-blur-sm">
          {/* Left Team */}
          <TeamScoreBox 
            teamName={team1.name}
            score={team1.score}
            overs={team1.overs}
            logo={team1.logo}
          />

          {/* Middle Event */}
          <EventBox event={currentEvent} />

          {/* Right Team */}
          <TeamScoreBox 
            teamName={team2.name}
            score={team2.score}
            overs={team2.overs}
            logo={team2.logo}
            isReversed={true}
          />
        </div>
      </WavyBackground>

      {/* Additional Info Box - Centered below */}
      {additionalInfo && (
        <div className="flex justify-center">
          <AdditionalInfoBox additionalInfo={additionalInfo} />
        </div>
      )}
    </div>
  );
};

export default ScoreboardRow;
