import React from 'react';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';
import { BackgroundGradient } from '../../ui/background-gradient';

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  return (
    <div className="flex flex-col gap-4">
      {/* Main Score Row */}
      <BackgroundGradient
        className="w-full"
        containerClassName="w-full"
        animate={true}
        gradientColors={{
          start: "rgba(59, 130, 246, 0.7)", // Blue
          middle: "rgba(99, 102, 241, 0.7)", // Indigo
          end: "rgba(139, 92, 246, 0.7)",    // Purple
          base: "rgba(30, 58, 138, 0.7)"     // Darker blue
        }}
      >
        <div className="flex items-center justify-between px-8 py-4">
          {/* Left Team */}
          <TeamScoreBox 
            teamName={team1.name}
            score={team1.score}
            overs={team1.overs}
            logo={team1.logo}
          />

          {/* Middle Event */}
          <EventBox event={currentEvent} />

          {/* Right Team */}
          <TeamScoreBox 
            teamName={team2.name}
            score={team2.score}
            overs={team2.overs}
            logo={team2.logo}
            isReversed={true}
          />
        </div>
      </BackgroundGradient>

      {/* Additional Info Box - Centered below */}
      {additionalInfo && (
        <div className="flex justify-center">
          <BackgroundGradient
            className="w-auto"
            containerClassName="w-auto"
            animate={true}
            gradientColors={{
              start: "rgba(59, 130, 246, 0.5)",
              middle: "rgba(99, 102, 241, 0.5)",
              end: "rgba(139, 92, 246, 0.5)",
              base: "rgba(30, 58, 138, 0.5)"
            }}
          >
            <AdditionalInfoBox additionalInfo={additionalInfo} />
          </BackgroundGradient>
        </div>
      )}
    </div>
  );
};

export default ScoreboardRow;
