import React, { useState, useEffect } from 'react';

const SpeedBox = ({ speedData }) => {
  const [currentBall, setCurrentBall] = useState({
    runs: '',
    speed: ''
  });

  useEffect(() => {
    if (speedData?.type === 'speedUpdate' && speedData?.data?.latestBall) {
      const ball = speedData.data.latestBall;
      setCurrentBall({
        runs: ball.Runs || '0',
        speed: ball.Ball_Speed || ''
      });
    }
    else if (speedData?.length > 0) {
      const latestBall = speedData[0];
      setCurrentBall({
        runs: latestBall.Runs || '0',
        speed: latestBall.Ball_Speed || ''
      });
    }
  }, [speedData]);

  return (
    <div className="flex-1 relative group min-w-fit
                    bg-gradient-to-br from-[#0f1423] via-[#151b2e] to-[#0f1423]
                    backdrop-blur-xl backdrop-saturate-150
                    border border-blue-400/20
                    shadow-[rgba(0,0,0,0.56)_0px_22px_70px_4px]
                    rounded-xl">
      <div className="flex items-center justify-between py-[0.7rem] px-4 relative w-full">
        <div className="flex items-center gap-2">
          <span className="text-xl font-bold text-[#e5e5e5] whitespace-nowrap">
            {currentBall.runs && `${currentBall.runs}r`}
          </span>
          <span className="text-xl font-bold text-[#e5e5e5] whitespace-nowrap">
            {currentBall.speed && `${currentBall.speed}`}
          </span>
        </div>
        <div className="px-3 py-2 rounded-lg
                     bg-gradient-to-br from-[#0f1423]/98 to-[#151b2e]/98
                     border border-blue-400/20">
          <span className="text-3xl font-medium whitespace-nowrap
                       text-[#e5e5e5] leading-none
                       drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]">
            BALL
          </span>
        </div>
      </div>
    </div>
  );
};

export default SpeedBox;
