import { cn } from "../../lib/utils";
import React from "react";
import { motion } from "framer-motion";
 
export const BackgroundGradient = ({
  children,
  className,
  containerClassName,
  animate = true,
  gradientColors = {
    start: "#3b82f6",
    middle: "#60a5fa",
    end: "#93c5fd",
    base: "#1d4ed8"
  }
}) => {
  const variants = {
    initial: {
      backgroundPosition: "0 50%",
    },
    animate: {
      backgroundPosition: ["0, 50%", "100% 50%", "0 50%"],
    },
  };

  const glowVariants = {
    initial: { opacity: 0.4, scale: 1 },
    animate: {
      opacity: [0.4, 0.5, 0.4],
      scale: [1, 1.05, 1],
      transition: {
        duration: 3,
        repeat: Infinity,
        repeatType: "reverse",
      }
    }
  };

  const borderVariants = {
    initial: { 
      rotate: 0,
      scale: 1
    },
    animate: {
      rotate: [0, 1, 0, -1, 0],
      scale: [1, 1.02, 1, 1.02, 1],
      transition: {
        duration: 5,
        repeat: Infinity,
        repeatType: "reverse",
        ease: "easeInOut"
      }
    }
  };

  const gradientStyle = `radial-gradient(circle farthest-side at 0 100%,${gradientColors.start},transparent),
                        radial-gradient(circle farthest-side at 100% 0,${gradientColors.middle},transparent),
                        radial-gradient(circle farthest-side at 100% 100%,${gradientColors.end},transparent),
                        radial-gradient(circle farthest-side at 0 0,${gradientColors.base},#141316)`;

  return (
    <motion.div 
      className={cn("relative p-[2px] group", containerClassName)}
      variants={borderVariants}
      initial="initial"
      animate="animate"
      whileHover={{ scale: 1.005 }}
      transition={{ duration: 0.2 }}
    >
      {/* Outer glow */}
      <motion.div
        variants={glowVariants}
        initial="initial"
        animate="animate"
        style={{
          backgroundSize: animate ? "400% 400%" : undefined,
          background: gradientStyle
        }}
        className={cn(
          "absolute inset-0 rounded-xl z-[1] blur-xl transition duration-500",
          "group-hover:opacity-75 group-hover:scale-105",
          "motion-reduce:transition-none motion-reduce:hover:transform-none"
        )}
      />

      {/* Main border gradient */}
      <motion.div
        variants={animate ? variants : undefined}
        initial={animate ? "initial" : undefined}
        animate={animate ? "animate" : undefined}
        transition={
          animate
            ? {
                duration: 5,
                repeat: Infinity,
                repeatType: "reverse",
              }
            : undefined
        }
        style={{
          backgroundSize: animate ? "400% 400%" : undefined,
          background: gradientStyle
        }}
        className={cn(
          "absolute inset-0 rounded-xl z-[1]",
          "group-hover:opacity-90 group-hover:scale-[1.01]",
          "transition-all duration-500"
        )}
      />

      {/* Floating particles effect */}
      <div className="absolute inset-0 z-[2] overflow-hidden rounded-xl opacity-50">
        <div className="absolute w-2 h-2 bg-white/10 rounded-full animate-float-particle-1" />
        <div className="absolute w-2 h-2 bg-white/10 rounded-full animate-float-particle-2" />
        <div className="absolute w-1 h-1 bg-white/10 rounded-full animate-float-particle-3" />
      </div>

      {/* 3D effect layers with movement */}
      <motion.div 
        className="absolute inset-[1px] rounded-[11px] bg-gradient-to-b from-white/5 to-transparent z-[2]"
        animate={{
          opacity: [0.3, 0.4, 0.3],
        }}
        transition={{
          duration: 2,
          repeat: Infinity,
          repeatType: "reverse",
        }}
      />
      <motion.div 
        className="absolute inset-[1px] rounded-[11px] bg-gradient-to-t from-black/20 to-transparent z-[2]"
        animate={{
          opacity: [0.2, 0.3, 0.2],
        }}
        transition={{
          duration: 2,
          repeat: Infinity,
          repeatType: "reverse",
          delay: 1
        }}
      />
      
      {/* Content container with inner shadow */}
      <div className={cn(
        "relative z-10 rounded-[11px]",
        "bg-gradient-to-b from-black/90 via-black/80 to-black/90",
        "shadow-[inset_0_1px_1px_rgba(255,255,255,0.1),inset_0_-1px_1px_rgba(0,0,0,0.8)]",
        "backdrop-blur-[1px]",
        className
      )}>
        {children}
      </div>
    </motion.div>
  );
};
