import React, { useState, useEffect } from 'react';

const SpeedBox = ({ speedData }) => {
  // Use sample data as default
  const [currentBall, setCurrentBall] = useState({
    over: '-',
    speed: '150kmph'  // Including 'kmph' since we handle it in getSpeedNumber
  });

  // Animation state
  const [animationProgress, setAnimationProgress] = useState(0);

  // Add this state for speed change direction
  const [speedTrend, setSpeedTrend] = useState('');

  // Add new state for animated speed value
  const [animatedSpeed, setAnimatedSpeed] = useState(0);

  // Add animation control state
  const [isAnimating, setIsAnimating] = useState(false);

  // Convert speed string to number (remove 'kmph' and parse)
  const getSpeedNumber = (speedString) => {
    return parseInt(speedString?.replace('kmph', '') || '0');
  };

  // Calculate color based on speed
  const getSpeedColor = (speed) => {
    const speedNum = getSpeedNumber(speed);
    if (speedNum >= 140) return '#FF0000'; // Red for very fast
    if (speedNum >= 130) return '#FF6B00'; // Orange for fast
    if (speedNum >= 120) return '#FFA500'; // Yellow-Orange for medium
    return '#FFD700'; // Yellow for slower
  };

  // Function to animate speed
  const animateSpeedometer = (targetSpeed) => {
    setIsAnimating(true);
    const startTime = Date.now();
    const duration = 5000; // 5 seconds total
    const midPoint = duration / 2; // 2.5 seconds to reach target

    const animate = () => {
      const currentTime = Date.now();
      const elapsed = currentTime - startTime;

      if (elapsed < duration) {
        // First half: go up to target speed
        if (elapsed < midPoint) {
          const progress = elapsed / midPoint;
          setAnimatedSpeed(Math.round(targetSpeed * progress));
        } 
        // Second half: return to zero
        else {
          const progress = (duration - elapsed) / midPoint;
          setAnimatedSpeed(Math.round(targetSpeed * progress));
        }
        requestAnimationFrame(animate);
      } else {
        setAnimatedSpeed(0);
        setIsAnimating(false);
      }
    };

    requestAnimationFrame(animate);
  };

  // Trigger animation when speed changes
  useEffect(() => {
    if (speedData && speedData[0]) {
      const newSpeed = getSpeedNumber(speedData[0].Ball_Speed);
      if (!isAnimating && newSpeed > 0) {
        animateSpeedometer(newSpeed);
      }
      setCurrentBall({
        over: speedData[0].Over || '2.6',
        speed: speedData[0].Ball_Speed || '132kmph'
      });
    }
  }, [speedData]);

  // Update the useEffect to track speed changes
  useEffect(() => {
    if (speedData?.Ball_Speed) {
      const newSpeed = getSpeedNumber(speedData.Ball_Speed);
      const oldSpeed = getSpeedNumber(currentBall.speed);

      // Set trend arrow based on speed change
      if (newSpeed > oldSpeed) setSpeedTrend('↑');
      else if (newSpeed < oldSpeed) setSpeedTrend('↓');
      else setSpeedTrend('');

      // Reset trend after 2 seconds
      const trendTimer = setTimeout(() => setSpeedTrend(''), 2000);

      // Start progress animation
      setAnimationProgress(0);
      const progressTimer = setInterval(() => {
        setAnimationProgress(prev => {
          if (prev >= 100) {
            clearInterval(progressTimer);
            return 100;
          }
          return prev + 5;
        });
      }, 20);

      return () => {
        clearTimeout(trendTimer);
        clearInterval(progressTimer);
      };
    }
  }, [speedData?.Ball_Speed]);

  const speed = speedData?.Ball_Speed || currentBall.speed;
  const speedColor = getSpeedColor(speed);

  // Add this function to calculate needle rotation based on speed
  const calculateNeedleRotation = () => {
    const clampedSpeed = Math.min(Math.max(animatedSpeed, 0), 160);
    return -90 + (clampedSpeed * 180 / 160);
  };

  return (
    <div className="fixed inset-0 z-50">
      {/* Semi-transparent backdrop */}
      <div className="absolute right-[1.5rem] top-[580px] w-[320px] h-[64px]
                      bg-[#0d1631]/80 backdrop-blur-sm rounded-xl
                      transition-opacity duration-300
                      transform translate-y-[104px]" />

      {/* Speed display */}
      <div className="absolute right-[1.5rem] top-[580px] w-[320px] h-[64px]
                      bg-gradient-to-br from-[#0f1423] via-[#151b2e] to-[#0f1423]
                      backdrop-blur-xl backdrop-saturate-150
                      border border-blue-400/20
                      shadow-[rgba(0,0,0,0.56)_0px_22px_70px_4px]
                      rounded-xl
                      flex items-center gap-4
                      px-4
                      transform translate-y-[104px]
                      overflow-hidden">

        {/* Over Number in Animated Circle */}
        <div className="relative flex items-center">
          <div className="relative flex">
            {/* Calculate width based on content */}
            <div className="relative w-[calc(1.5ch_+_48px)] h-[48px]">
              {/* Outer rotating ring */}
              <div className="absolute inset-0 rounded-full border-2 border-blue-500/30 
                           animate-[spin_3s_linear_infinite]" />
              {/* Inner pulsing ring */}
              <div className="absolute inset-0 rounded-full border-2 border-blue-400/50
                           animate-[ping_2s_cubic-bezier(0,0,0.2,1)_infinite]" />
              {/* Background circle */}
              <div className="absolute inset-0 rounded-full bg-blue-500/10" />
              {/* Over number */}
              <div className="absolute inset-0 flex items-center justify-center
                           text-3xl font-medium text-[#e5e5e5] leading-none
                           px-3 whitespace-nowrap">
                {speedData?.Over || currentBall.over}
              </div>
            </div>
          </div>
        </div>

        {/* Speedometer Arc */}
        <div className="relative w-[120px] h-[60px]">
          {/* Glow background for depth */}
          <div 
            className="absolute inset-0 blur-lg"
            style={{ 
              background: `radial-gradient(circle at 50% 100%, ${speedColor}40 0%, transparent 70%)`,
              transform: 'translateY(4px)'
            }} 
          />

          <svg viewBox="0 0 100 50" className="w-full h-full relative">
            {/* Outer glow effect */}
            <filter id="glow">
              <feGaussianBlur stdDeviation="2" result="coloredBlur"/>
              <feMerge>
                <feMergeNode in="coloredBlur"/>
                <feMergeNode in="SourceGraphic"/>
              </feMerge>
            </filter>

            {/* Background Arc with gradient */}
            <defs>
              <linearGradient id="arcGradient" x1="0%" y1="0%" x2="100%" y2="0%">
                <stop offset="0%" style={{ stopColor: '#2A2A2A', stopOpacity: 0.3 }}/>
                <stop offset="50%" style={{ stopColor: '#3A3A3A', stopOpacity: 0.5 }}/>
                <stop offset="100%" style={{ stopColor: '#2A2A2A', stopOpacity: 0.3 }}/>
              </linearGradient>
            </defs>

            {/* Background Arc */}
            <path
              d="M10 40 A 40 40 0 0 1 90 40"
              fill="none"
              stroke="url(#arcGradient)"
              strokeWidth="6"
              strokeLinecap="round"
              filter="url(#glow)"
            />

            {/* Colored Arc with enhanced glow */}
            <path
              d="M10 40 A 40 40 0 0 1 90 40"
              fill="none"
              stroke={speedColor}
              strokeWidth="6"
              strokeLinecap="round"
              strokeDasharray="126"
              strokeDashoffset={126 - (126 * animationProgress / 100)}
              style={{ 
                transition: 'stroke-dashoffset 0.3s ease-out',
                filter: 'url(#glow)',
                opacity: 0.8
              }}
            />

            {/* Center point */}
            <circle
              cx="50"
              cy="40"
              r="3"
              fill={speedColor}
              filter="url(#glow)"
            />

            {/* Needle */}
            <g
              style={{
                transform: `rotate(${calculateNeedleRotation()}deg)`,
                transformOrigin: '50px 40px',
                transition: 'none' // Remove transition to allow smooth animation
              }}
            >
              {/* Needle body with gradient */}
              <defs>
                <linearGradient id="needleGradient" x1="0%" y1="0%" x2="0%" y2="100%">
                  <stop offset="0%" stopColor={speedColor} stopOpacity="1"/>
                  <stop offset="100%" stopColor={speedColor} stopOpacity="0.6"/>
                </linearGradient>
              </defs>

              {/* Needle shadow for depth */}
              <line
                x1="50"
                y1="41"
                x2="50"
                y2="21"
                stroke="rgba(0,0,0,0.3)"
                strokeWidth="3"
                filter="url(#glow)"
                style={{ transition: 'all 0.3s ease' }}
              />

              {/* Needle body */}
              <line
                x1="50"
                y1="40"
                x2="50"
                y2="20"
                stroke="url(#needleGradient)"
                strokeWidth="2"
                filter="url(#glow)"
                style={{ transition: 'all 0.3s ease' }}
              />

              {/* Needle head */}
              <polygon
                points="48,20 52,20 50,15"
                fill={speedColor}
                filter="url(#glow)"
                style={{ transition: 'all 0.3s ease' }}
              />

              {/* Add subtle wobble animation on speed change */}
              <animate
                attributeName="transform"
                type="rotate"
                values="0,50,40; 2,50,40; -1,50,40; 0,50,40"
                dur="0.5s"
                begin="0s"
                repeatCount="1"
                calcMode="spline"
                keySplines="0.4 0 0.2 1; 0.4 0 0.2 1; 0.4 0 0.2 1"
                key={speed} // Force animation to restart on speed change
              />
            </g>

            {/* Tick marks */}
            {[...Array(9)].map((_, i) => (
              <g key={i} transform={`rotate(${i * 22.5 - 90}, 50, 40)`}>
                <line
                  x1="50"
                  y1="15"
                  x2="50"
                  y2={i % 2 === 0 ? "20" : "18"}
                  stroke="#4A4A4A"
                  strokeWidth={i % 2 === 0 ? "2" : "1"}
                  opacity={i % 2 === 0 ? "0.5" : "0.3"}
                />
                {i % 2 === 0 && (
                  <text
                    x="50"
                    y="13"
                    fill="#666"
                    fontSize="6"
                    textAnchor="middle"
                    transform={`rotate(${90 - i * 22.5}, 50, 13)`}
                  >
                    {Math.round(i * 20)}
                  </text>
                )}
              </g>
            ))}
          </svg>
        </div>

        {/* Speed Text with Animation */}
        <div className="flex items-baseline gap-1 ml-auto relative">
          {/* Background glow effect */}
          <div 
            className="absolute inset-0 blur-xl opacity-30"
            style={{ 
              background: `radial-gradient(circle, ${speedColor} 0%, transparent 70%)`,
              animation: 'pulse 2s infinite'
            }}
          />

          {/* Speed Number */}
          <div className="relative flex items-baseline">
            <span 
              className="text-4xl font-bold relative"
              style={{ 
                color: speedColor,
                textShadow: `0 0 10px ${speedColor}`
              }}
            >
              {animatedSpeed}
            </span>

            {/* Unit (kmph) with floating effect */}
            <span 
              className="text-2xl opacity-70 ml-2 relative"
              style={{ 
                color: speedColor,
                animation: 'float 3s ease-in-out infinite'
              }}
            >
              kmph
            </span>
          </div>
        </div>

        {/* Add these styles to your CSS or in a style tag */}
        <style jsx>{`
          @keyframes speedPulse {
            0% { transform: scale(1); }
            50% { transform: scale(1.05); }
            100% { transform: scale(1); }
          }

          @keyframes float {
            0% { transform: translateY(0px); }
            50% { transform: translateY(-3px); }
            100% { transform: translateY(0px); }
          }

          @keyframes pulse {
            0% { opacity: 0.2; }
            50% { opacity: 0.4; }
            100% { opacity: 0.2; }
          }
        `}</style>

        {/* Progress Bar */}
        <div className="absolute left-0 bottom-0 w-full h-1">
          <div 
            className="h-full"
            style={{
              width: `${animationProgress}%`,
              backgroundColor: speedColor,
              transition: 'all 0.3s ease-out',
              boxShadow: `0 0 10px ${speedColor}`
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default SpeedBox;
