import React from 'react';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';
import { BackgroundGradientAnimation } from '../../../components/ui/background-gradient-animation';

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  return (
    <div className="flex flex-col gap-4">
      {/* Main Score Row */}
      <BackgroundGradientAnimation
        gradientBackgroundStart="rgb(0, 17, 82)"
        gradientBackgroundEnd="rgb(10, 10, 40)"
        firstColor="18, 113, 255"
        secondColor="100, 220, 255"
        thirdColor="221, 74, 255"
        fourthColor="100, 220, 255"
        fifthColor="40, 80, 255"
        className="rounded-lg border border-blue-400/20"
        containerClassName="w-full h-auto rounded-lg"
        interactive={false}
        blendingValue="screen"
      >
        <div className="w-full flex items-center justify-between px-8 py-4
                      premium-box-shadow content-box-glow
                      backdrop-blur-sm bg-black/30">
          {/* Left Team */}
          <TeamScoreBox 
            teamName={team1.name}
            score={team1.score}
            overs={team1.overs}
            logo={team1.logo}
          />

          {/* Middle Event */}
          <EventBox event={currentEvent} />

          {/* Right Team */}
          <TeamScoreBox 
            teamName={team2.name}
            score={team2.score}
            overs={team2.overs}
            logo={team2.logo}
            isReversed={true}
          />
        </div>
      </BackgroundGradientAnimation>

      {/* Additional Info Box - Centered below */}
      {additionalInfo && (
        <div className="flex justify-center">
          <AdditionalInfoBox additionalInfo={additionalInfo} />
        </div>
      )}
    </div>
  );
};

export default ScoreboardRow;
