import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { BackgroundGradientAnimation } from '../../ui/background-gradient-animation';

const TeamScoreBox = ({ teamName, score, overs, logo, isReversed = false }) => {
  const [prevScore, setPrevScore] = useState(score);
  const [isScoreIncreased, setIsScoreIncreased] = useState(false);

  // Format team name if it has 3 or more words
  const formatTeamName = (name) => {
    if (!name) return '';

    const cleanName = name.trim().replace(/\s+/g, ' ');
    const words = cleanName.split(' ').filter(word => word.length > 0);

    if (words.length >= 4) {
      const initials = words
        .map(word => word.charAt(0))
        .join('')
        .toUpperCase();
      return initials;
    }

    return cleanName;
  };

  const displayName = formatTeamName(teamName);

  // Track score changes for animation
  useEffect(() => {
    if (score !== prevScore) {
      setIsScoreIncreased(true);
      setPrevScore(score);

      const timer = setTimeout(() => {
        setIsScoreIncreased(false);
      }, 1000);

      return () => clearTimeout(timer);
    }
  }, [score]);

  // Enhanced animation variants
  const scoreVariants = {
    initial: {
      scale: 1,
      color: '#e5e5e5'
    },
    increased: {
      scale: [1, 1.2, 1],
      color: ['#e5e5e5', '#4ade80', '#e5e5e5'],
      transition: { 
        duration: 0.5,
        ease: "easeOut"
      }
    }
  };

  const logoVariants = {
    initial: {
      scale: 1,
      filter: 'drop-shadow(0 0 0px rgba(255,255,255,0))'
    },
    hover: {
      scale: 1.1,
      filter: 'drop-shadow(0 0 12px rgba(255,255,255,0.5))',
      transition: { duration: 0.3 }
    }
  };

  return (
    <BackgroundGradientAnimation
      gradientBackgroundStart="rgb(13, 22, 49)"
      gradientBackgroundEnd="rgb(26, 36, 66)"
      firstColor="18, 113, 255"
      secondColor="100, 0, 200"
      thirdColor="0, 40, 100"
      fourthColor="20, 0, 60"
      fifthColor="0, 20, 80"
      pointerColor="80, 100, 255"
      size="120%"
      blendingValue="overlay"
      containerClassName="rounded-lg relative overflow-hidden"
    >
      {/* Main Score Row */}
      <div className="w-full flex items-center justify-between relative z-10
                    rounded-lg border border-blue-400/20
                    bg-black/20 backdrop-blur-sm">
        <div className={`flex items-center gap-6 p-4 ${isReversed ? 'flex-row-reverse' : 'flex-row'}`}>
          <motion.div 
            className="w-12 h-12 flex-shrink-0 relative"
            initial="initial"
            whileHover="hover"
            variants={logoVariants}
          >
            {logo && (
              <motion.img 
                src={logo} 
                alt="" 
                className="w-full h-full object-contain"
                variants={logoVariants}
              />
            )}
            {/* Glow effect */}
            <div className="absolute inset-0 bg-blue-500/20 rounded-full filter blur-md -z-10" />
          </motion.div>

          <div className={`flex items-baseline gap-3 ${isReversed ? 'flex-row-reverse' : 'flex-row'}`}>
            <motion.div 
              className="text-2xl font-bold text-[#e5e5e5] drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]" 
              title={teamName}
              whileHover={{ scale: 1.05 }}
              transition={{ type: "spring", stiffness: 400, damping: 10 }}
            >
              {displayName}
            </motion.div>
            <div className="flex items-baseline gap-2">
              <motion.span 
                className="text-8xl font-bold text-[#e5e5e5] tracking-tight leading-none
                         drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]"
                initial="initial"
                animate={isScoreIncreased ? "increased" : "initial"}
                variants={scoreVariants}
              >
                {score}
              </motion.span>
              <AnimatePresence mode="wait">
                {overs && (
                  <motion.span 
                    className="text-2xl text-blue-300/80 relative group"
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -10 }}
                    transition={{ duration: 0.3 }}
                  >
                    ({overs} ov)
                    <span className="absolute inset-0 bg-blue-400/10 rounded-lg -z-10 
                                   opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                  </motion.span>
                )}
              </AnimatePresence>
            </div>
          </div>
        </div>
      </div>

      {/* Add optimized animation styles */}
      <style jsx>{`
        .rotating-background {
          position: absolute;
          width: 200%;
          height: 200%;
          top: -50%;
          left: -50%;
          background: linear-gradient(45deg, 
            rgba(0,0,139,0.7), 
            rgba(20,3,3,0.7),
            rgba(0,0,60,0.7)
          );
          filter: blur(80px);
          animation: seamlessRotateAndMove 30s linear infinite;
          transform-origin: center center;
          pointer-events: none;
          background-size: 200% 200%;
          mix-blend-mode: overlay;
          will-change: transform;
        }

        @keyframes seamlessRotateAndMove {
          0% { 
            transform: rotate(0deg) scale(1);
          }
          100% { 
            transform: rotate(360deg) scale(1);
          }
        }

        .glow-box::before {
          content: '';
          position: absolute;
          inset: -1px;
          background: linear-gradient(
            45deg,
            transparent,
            rgba(59, 130, 246, 0.1),
            transparent
          );
          z-index: -1;
          border-radius: inherit;
          animation: borderGlow 6s linear infinite;
        }

        @keyframes borderGlow {
          0%, 100% {
            opacity: 0.3;
          }
          50% {
            opacity: 0.6;
          }
        }
      `}</style>
    </BackgroundGradientAnimation>
  );
};

export default TeamScoreBox;
