import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const SpeedBox = ({ speedData }) => {
  // Extract speed from Ball_Speed (remove 'kph' from the end)
  const currentSpeed = speedData?.Ball_Speed?.replace('kph', '') || '0';

  // Parse Ball_Speed_This_Over from string to array
  const overSpeedsString = speedData?.Ball_Speed_This_Over || '""';
  const overSpeeds = JSON.parse(`[${overSpeedsString}]`)
    .map(speed => speed.replace('kph', ''))
    .filter(speed => speed !== '');

  // Get the maximum speed from current over
  const maxSpeedThisOver = Math.max(...overSpeeds.map(speed => parseFloat(speed) || 0));

  return (
    <div className="speed-box">
      <div className="current-speed">
        <h3>Current Speed</h3>
        <div className="speed-value">{currentSpeed}</div>
        <div className="unit">KPH</div>
      </div>
      <div className="max-speed">
        <h3>Max Speed This Over</h3>
        <div className="speed-value">{maxSpeedThisOver.toFixed(1)}</div>
        <div className="unit">KPH</div>
      </div>
      <div className="over-details">
        <h3>Over: {speedData?.Over || '0.0'}</h3>
        <p>Bowler: {speedData?.Bowler_Short_Name || 'N/A'}</p>
        <p>Runs: {speedData?.Runs || '0'}</p>
      </div>
    </div>
  );
};

export default SpeedBox;
