import { cn } from "../../utils/cn";
import React, { useEffect, useRef, useState, useCallback } from "react";

// Star interface
const createStar = (x, y, radius, opacity, twinkleSpeed) => ({
  x,
  y,
  radius,
  opacity,
  twinkleSpeed: twinkleSpeed !== null ? twinkleSpeed : null
});

// Shooting star interface
const getRandomStartPoint = () => {
  const side = Math.floor(Math.random() * 4);
  const offset = Math.random() * window.innerWidth;
  
  switch (side) {
    case 0: // Top
      return { x: offset, y: 0, angle: 45 };
    case 1: // Right
      return { x: window.innerWidth, y: offset, angle: 135 };
    case 2: // Bottom
      return { x: offset, y: window.innerHeight, angle: 225 };
    case 3: // Left
      return { x: 0, y: offset, angle: 315 };
    default:
      return { x: 0, y: 0, angle: 45 };
  }
};

export const BackgroundGradientAnimation = ({
  gradientBackgroundStart = "rgb(13, 22, 49)",
  gradientBackgroundEnd = "rgb(26, 39, 81)",
  firstColor = "59, 130, 246",
  secondColor = "30, 64, 175",
  thirdColor = "17, 24, 39",
  starDensity = 0.00015,
  allStarsTwinkle = true,
  twinkleProbability = 0.7,
  minTwinkleSpeed = 0.5,
  maxTwinkleSpeed = 1,
  shootingStarConfig = {
    minSpeed: 10,
    maxSpeed: 30,
    minDelay: 1200,
    maxDelay: 4200,
    starColor: "#9E00FF",
    trailColor: "#2EB9DF",
    starWidth: 10,
    starHeight: 1
  },
  size = "100%",
  className,
  interactive = false,
  containerClassName,
  children,
}) => {
  const canvasRef = useRef(null);
  const [stars, setStars] = useState([]);
  const [shootingStar, setShootingStar] = useState(null);

  // Generate initial stars
  const generateStars = useCallback((width, height) => {
    const area = width * height;
    const numStars = Math.floor(area * starDensity);
    return Array.from({ length: numStars }, () => {
      const shouldTwinkle = allStarsTwinkle || Math.random() < twinkleProbability;
      return createStar(
        Math.random() * width,
        Math.random() * height,
        Math.random() * 0.05 + 0.5,
        Math.random() * 0.5 + 0.5,
        shouldTwinkle ? minTwinkleSpeed + Math.random() * (maxTwinkleSpeed - minTwinkleSpeed) : null
      );
    });
  }, [starDensity, allStarsTwinkle, twinkleProbability, minTwinkleSpeed, maxTwinkleSpeed]);

  // Initialize canvas and stars
  useEffect(() => {
    if (!canvasRef.current) return;
    
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const { width, height } = canvas.getBoundingClientRect();
    
    canvas.width = width;
    canvas.height = height;
    setStars(generateStars(width, height));

    const resizeObserver = new ResizeObserver(() => {
      canvas.width = canvas.getBoundingClientRect().width;
      canvas.height = canvas.getBoundingClientRect().height;
      setStars(generateStars(canvas.width, canvas.height));
    });

    resizeObserver.observe(canvas);
    return () => resizeObserver.disconnect();
  }, [generateStars]);

  // Create new shooting stars periodically
  useEffect(() => {
    const createNewShootingStar = () => {
      const { x, y, angle } = getRandomStartPoint();
      const speed = Math.random() * 
        (shootingStarConfig.maxSpeed - shootingStarConfig.minSpeed) + 
        shootingStarConfig.minSpeed;

      setShootingStar({
        id: Date.now(),
        x,
        y,
        angle,
        scale: 1,
        speed,
        distance: 0
      });

      const delay = Math.random() * 
        (shootingStarConfig.maxDelay - shootingStarConfig.minDelay) + 
        shootingStarConfig.minDelay;
      
      setTimeout(createNewShootingStar, delay);
    };

    createNewShootingStar();
  }, [shootingStarConfig]);

  // Animation loop
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    let animationFrameId;

    const render = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // Draw stars
      stars.forEach(star => {
        ctx.beginPath();
        ctx.arc(star.x, star.y, star.radius, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(255, 255, 255, ${star.opacity})`;
        ctx.fill();

        // Twinkle effect
        if (star.twinkleSpeed !== null) {
          star.opacity = 0.5 + Math.abs(Math.sin(Date.now() * 0.001 / star.twinkleSpeed) * 0.5);
        }
      });

      // Draw shooting star
      if (shootingStar) {
        ctx.save();
        ctx.translate(shootingStar.x, shootingStar.y);
        ctx.rotate((shootingStar.angle * Math.PI) / 180);
        
        const gradient = ctx.createLinearGradient(
          0, 0, 
          shootingStarConfig.starWidth * shootingStar.scale, 0
        );
        gradient.addColorStop(0, `${shootingStarConfig.trailColor}00`);
        gradient.addColorStop(1, shootingStarConfig.starColor);
        
        ctx.fillStyle = gradient;
        ctx.fillRect(
          0, 0, 
          shootingStarConfig.starWidth * shootingStar.scale, 
          shootingStarConfig.starHeight
        );
        ctx.restore();

        // Update shooting star position
        setShootingStar(prev => {
          if (!prev) return null;
          const newX = prev.x + prev.speed * Math.cos((prev.angle * Math.PI) / 180);
          const newY = prev.y + prev.speed * Math.sin((prev.angle * Math.PI) / 180);
          const newDistance = prev.distance + prev.speed;
          
          if (newX < -20 || newX > canvas.width + 20 || 
              newY < -20 || newY > canvas.height + 20) {
            return null;
          }
          
          return {
            ...prev,
            x: newX,
            y: newY,
            distance: newDistance,
            scale: 1 + newDistance / 100
          };
        });
      }

      animationFrameId = requestAnimationFrame(render);
    };

    render();
    return () => cancelAnimationFrame(animationFrameId);
  }, [stars, shootingStar, shootingStarConfig.starWidth, shootingStarConfig.starHeight, 
      shootingStarConfig.trailColor, shootingStarConfig.starColor]);

  return (
    <div className={cn(
      "absolute inset-0 w-[1920px] h-[1080px] overflow-hidden",
      "bg-[linear-gradient(40deg,var(--gradient-background-start),var(--gradient-background-end))]",
      containerClassName
    )}>
      <canvas
        ref={canvasRef}
        className={cn("absolute inset-0 w-[1920px] h-[1080px]", className)}
      />
      <div className={cn(
        "absolute inset-0 w-[1920px] h-[1080px]",
        "bg-[radial-gradient(circle_at_center,_rgba(var(--first-color),_0.8)_0,_rgba(var(--first-color),_0)_50%)]",
        "mix-blend-hard-light"
      )} />
      <div className={cn(
        "absolute inset-0 w-[1920px] h-[1080px]",
        "bg-[radial-gradient(circle_at_center,_rgba(var(--second-color),_0.8)_0,_rgba(var(--second-color),_0)_50%)]",
        "mix-blend-hard-light"
      )} />
      <div className={cn(
        "absolute inset-0 w-[1920px] h-[1080px]",
        "bg-[radial-gradient(circle_at_center,_rgba(var(--third-color),_0.8)_0,_rgba(var(--third-color),_0)_50%)]",
        "mix-blend-hard-light"
      )} />
      {children}
    </div>
  );
};
