import React from 'react';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';
import { BackgroundGradientAnimation } from '../../ui/background-gradient-animation';

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  return (
    <div className="flex flex-col gap-4">
      <BackgroundGradientAnimation
        gradientBackgroundStart="rgb(13, 22, 49)"
        gradientBackgroundEnd="rgb(26, 36, 66)"
        firstColor="18, 113, 255"
        secondColor="100, 0, 200"
        thirdColor="0, 40, 100"
        fourthColor="20, 0, 60"
        fifthColor="0, 20, 80"
        pointerColor="80, 100, 255"
        size="120%"
        blendingValue="overlay"
        containerClassName="rounded-lg relative overflow-hidden"
      >
        {/* Main Score Row */}
        <div className="w-full flex items-center justify-between px-8 py-4
                      rounded-lg border border-blue-400/20 relative z-10
                      bg-black/20 backdrop-blur-sm">
          {/* Left Team */}
          <TeamScoreBox 
            teamName={team1.name}
            score={team1.score}
            overs={team1.overs}
            logo={team1.logo}
          />

          {/* Middle Event */}
          <EventBox event={currentEvent} />

          {/* Right Team */}
          <TeamScoreBox 
            teamName={team2.name}
            score={team2.score}
            overs={team2.overs}
            logo={team2.logo}
            isReversed={true}
          />
        </div>
      </BackgroundGradientAnimation>

      {/* Additional Info Box - Centered below */}
      {additionalInfo && (
        <div className="flex justify-center">
          <AdditionalInfoBox additionalInfo={additionalInfo} />
        </div>
      )}
    </div>
  );
};

export default ScoreboardRow;
