import React, { useState, useEffect } from 'react';

const SpeedBox = ({ speedData }) => {
  // States
  const [currentBall, setCurrentBall] = useState({
    over: '-',
    speed: '130kmph'
  });
  const [animationProgress, setAnimationProgress] = useState(0);
  const [speedTrend, setSpeedTrend] = useState('');
  const [displaySpeed, setDisplaySpeed] = useState('0');
  const [needleAngle, setNeedleAngle] = useState(-90);

  // Helper functions
  const getSpeedNumber = (speedString) => {
    return parseInt(speedString?.replace('kmph', '') || '0');
  };

  const getSpeedColor = (speed) => {
    const speedNum = getSpeedNumber(speed);
    if (speedNum >= 140) return '#FF0000';
    if (speedNum >= 130) return '#FF6B00';
    if (speedNum >= 120) return '#FFA500';
    return '#FFD700';
  };

  const calculateNeedleAngle = (speed) => {
    const speedNum = getSpeedNumber(speed);
    return -90 + (speedNum * 180 / 160);
  };

  // Get current speed value - Move this before the effects
  const speed = speedData?.Ball_Speed || currentBall.speed;
  const speedColor = getSpeedColor(speed);

  // Effects
  useEffect(() => {
    if (speedData && speedData[0]) {
      setCurrentBall({
        over: speedData[0].Over || '2.6',
        speed: speedData[0].Ball_Speed || '132kmph'
      });
    }
  }, [speedData]);

  // Smooth animation effect for speed
  useEffect(() => {
    const targetSpeed = getSpeedNumber(speed);
    const startTime = Date.now();
    const duration = 3000;

    const animateSpeed = () => {
      const currentTime = Date.now();
      const elapsed = currentTime - startTime;
      const progress = Math.min(elapsed / duration, 1);

      const easeProgress = 1 - Math.pow(1 - progress, 4);

      // Animate speed number
      const currentSpeed = Math.round(targetSpeed * easeProgress);
      setDisplaySpeed(currentSpeed.toString());

      // Animate needle
      const targetAngle = calculateNeedleAngle(speed);
      const currentAngle = -90 + (targetAngle + 90) * easeProgress;
      setNeedleAngle(currentAngle);

      if (progress < 1) {
        requestAnimationFrame(animateSpeed);
      }
    };

    requestAnimationFrame(animateSpeed);
  }, [speed]);

  return (
    <div className="fixed inset-0 z-50">
      {/* Semi-transparent backdrop */}
      <div className="absolute right-[1.5rem] top-[580px] w-[320px] h-[64px]
                      bg-[#0d1631]/80 backdrop-blur-sm rounded-xl
                      transition-opacity duration-300
                      transform translate-y-[104px]" />

      {/* Speed display */}
      <div className="absolute right-[1.5rem] top-[580px] w-[320px] h-[64px]
                      bg-gradient-to-br from-[#0f1423] via-[#151b2e] to-[#0f1423]
                      backdrop-blur-xl backdrop-saturate-150
                      border border-blue-400/20
                      shadow-[rgba(0,0,0,0.56)_0px_22px_70px_4px]
                      rounded-xl
                      flex items-center gap-4
                      px-4
                      transform translate-y-[104px]
                      overflow-hidden">

        {/* Over Number in Animated Circle */}
        <div className="relative flex items-center">
          <div className="relative flex">
            {/* Calculate width based on content */}
            <div className="relative w-[calc(1.5ch_+_48px)] h-[48px]">
              {/* Outer rotating ring */}
              <div className="absolute inset-0 rounded-full border-2 border-blue-500/30 
                           animate-[spin_3s_linear_infinite]" />
              {/* Inner pulsing ring */}
              <div className="absolute inset-0 rounded-full border-2 border-blue-400/50
                           animate-[ping_2s_cubic-bezier(0,0,0.2,1)_infinite]" />
              {/* Background circle */}
              <div className="absolute inset-0 rounded-full bg-blue-500/10" />
              {/* Over number */}
              <div className="absolute inset-0 flex items-center justify-center
                           text-3xl font-medium text-[#e5e5e5] leading-none
                           px-3 whitespace-nowrap">
                {speedData?.Over || currentBall.over}
              </div>
            </div>
          </div>
        </div>

        {/* Speedometer Arc */}
        <div className="relative w-[120px] h-[60px] flex-shrink-0">
          {/* Enhanced background glow effect */}
          <div 
            className="absolute inset-0 blur-lg opacity-20"
            style={{ 
              background: `
                radial-gradient(circle at 50% 100%, ${speedColor} 0%, transparent 70%),
                radial-gradient(circle at 30% 100%, rgba(59,130,246,0.3) 0%, transparent 50%),
                radial-gradient(circle at 70% 100%, rgba(59,130,246,0.3) 0%, transparent 50%)
              `
            }} 
          />

          <svg 
            viewBox="0 0 100 50" 
            className="w-full h-full relative"
            preserveAspectRatio="xMidYMid meet"
          >
            <defs>
              {/* Enhanced gradient definitions */}
              <linearGradient id="arcGradient" x1="0%" y1="0%" x2="100%" y2="0%">
                <stop offset="0%" style={{ stopColor: '#2A2A2A', stopOpacity: 0.3 }}/>
                <stop offset="50%" style={{ stopColor: '#3A3A3A', stopOpacity: 0.5 }}/>
                <stop offset="100%" style={{ stopColor: '#2A2A2A', stopOpacity: 0.3 }}/>
              </linearGradient>

              {/* Enhanced glow filter */}
              <filter id="glow">
                <feGaussianBlur stdDeviation="1.5" result="coloredBlur"/>
                <feMerge>
                  <feMergeNode in="coloredBlur"/>
                  <feMergeNode in="SourceGraphic"/>
                </feMerge>
              </filter>

              {/* New metallic effect */}
              <linearGradient id="metallic" x1="0%" y1="0%" x2="0%" y2="100%">
                <stop offset="0%" stopColor="rgba(255,255,255,0.5)"/>
                <stop offset="50%" stopColor="rgba(255,255,255,0)"/>
                <stop offset="100%" stopColor="rgba(255,255,255,0.3)"/>
              </linearGradient>
            </defs>

            {/* Enhanced tick marks with numbers */}
            {[...Array(9)].map((_, i) => (
              <g key={i} transform={`rotate(${i * 22.5 - 90}, 50, 40)`}>
                <line
                  x1="50"
                  y1="15"
                  x2="50"
                  y2={i % 2 === 0 ? "18" : "16"}
                  stroke="#4A4A4A"
                  strokeWidth={i % 2 === 0 ? "1.5" : "1"}
                  opacity={i % 2 === 0 ? "0.4" : "0.2"}
                  vectorEffect="non-scaling-stroke"
                />
                {i % 2 === 0 && (
                  <text
                    x="50"
                    y="13"
                    fill="#666"
                    fontSize="4"
                    textAnchor="middle"
                    transform={`rotate(${90 - i * 22.5}, 50, 13)`}
                  >
                    {Math.round(i * 20)}
                  </text>
                )}
              </g>
            ))}

            {/* Additional decorative elements */}
            <path
              d="M10 42 A 40 40 0 0 1 90 42"
              fill="none"
              stroke="rgba(255,255,255,0.05)"
              strokeWidth="6"
              strokeLinecap="round"
              vectorEffect="non-scaling-stroke"
            />

            {/* Colored Arc - Fixed size with glow */}
            <path
              d="M10 40 A 40 40 0 0 1 90 40"
              fill="none"
              stroke={speedColor}
              strokeWidth="4"
              strokeLinecap="round"
              vectorEffect="non-scaling-stroke"
              filter="url(#glow)"
              style={{ opacity: 0.8 }}
            />

            {/* Enhanced needle with metallic effect */}
            <g
              style={{
                transform: `rotate(${needleAngle}deg)`,
                transformOrigin: '50px 40px'
              }}
            >
              {/* Needle shadow */}
              <line
                x1="50"
                y1="41"
                x2="50"
                y2="21"
                stroke="rgba(0,0,0,0.3)"
                strokeWidth="3"
                vectorEffect="non-scaling-stroke"
              />
              {/* Needle body with gradient */}
              <line
                x1="50"
                y1="40"
                x2="50"
                y2="20"
                stroke={speedColor}
                strokeWidth="2"
                vectorEffect="non-scaling-stroke"
                filter="url(#glow)"
              />
              {/* Metallic overlay */}
              <line
                x1="50"
                y1="40"
                x2="50"
                y2="20"
                stroke="url(#metallic)"
                strokeWidth="2"
                vectorEffect="non-scaling-stroke"
                opacity="0.5"
              />
              {/* Enhanced needle tip */}
              <polygon
                points="48,20 52,20 50,17"
                fill={speedColor}
                filter="url(#glow)"
              />
            </g>

            {/* Enhanced center dot with metallic effect */}
            <circle
              cx="50"
              cy="40"
              r="4"
              fill="#1A1A1A"
              vectorEffect="non-scaling-stroke"
            />
            <circle
              cx="50"
              cy="40"
              r="3"
              fill={speedColor}
              filter="url(#glow)"
              vectorEffect="non-scaling-stroke"
            />
            <circle
              cx="50"
              cy="40"
              r="3"
              fill="url(#metallic)"
              opacity="0.5"
              vectorEffect="non-scaling-stroke"
            />
          </svg>
        </div>

        {/* Speed Text with Animation */}
        <div className="flex items-baseline gap-1 ml-auto relative">
          {/* Background glow effect */}
          <div 
            className="absolute inset-0 blur-xl opacity-30"
            style={{ 
              background: `radial-gradient(circle, ${speedColor} 0%, transparent 70%)`,
              animation: 'pulse 2s infinite'
            }}
          />

          {/* Speed Number */}
          <div className="relative flex items-baseline">
            <span 
              className="text-4xl font-bold relative transform-gpu"
              style={{ 
                color: speedColor,
                textShadow: `0 0 10px ${speedColor}`,
                animation: 'textPulseAndScale 2s infinite'
              }}
            >
              {displaySpeed}

              {/* Speed change indicator */}
              {speedTrend && (
                <div 
                  className="absolute -right-3 top-0 text-lg transition-all duration-300"
                  style={{ 
                    color: speedColor,
                    opacity: speedTrend ? '0.7' : '0',
                    transform: `translateY(${speedTrend === '↑' ? '-2px' : '2px'})`,
                  }}
                >
                  {speedTrend}
                </div>
              )}
            </span>

            {/* Unit (kmph) with floating effect */}
            <span 
              className="text-2xl opacity-70 ml-2 relative"
              style={{ 
                color: speedColor,
                animation: 'float 3s ease-in-out infinite'
              }}
            >
              kmph
            </span>
          </div>
        </div>

        {/* Updated styles */}
        <style jsx>{`
          @keyframes pulse {
            0% { opacity: 0.2; }
            50% { opacity: 0.4; }
            100% { opacity: 0.2; }
          }

          @keyframes textPulseAndScale {
            0% { 
              opacity: 0.9;
              text-shadow: 0 0 10px currentColor;
              transform: scale(1);
            }
            50% { 
              opacity: 1;
              text-shadow: 0 0 20px currentColor;
              transform: scale(1.05);
            }
            100% { 
              opacity: 0.9;
              text-shadow: 0 0 10px currentColor;
              transform: scale(1);
            }
          }

          @keyframes float {
            0% { transform: translateY(0px); }
            50% { transform: translateY(-3px); }
            100% { transform: translateY(0px); }
          }
        `}</style>

        {/* Progress Bar */}
        <div className="absolute left-0 bottom-0 w-full h-1">
          <div 
            className="h-full"
            style={{
              width: `${animationProgress}%`,
              backgroundColor: speedColor,
              transition: 'all 0.3s ease-out',
              boxShadow: `0 0 10px ${speedColor}`
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default SpeedBox;
