import React, { useState } from 'react';

const SpeedBox = ({ speedData }) => {
  const [currentBall, setCurrentBall] = useState({
    over: '5',
    speed: ''
  });

  return (
    <div className="fixed inset-0 z-50">
      {/* Semi-transparent backdrop */}
      <div className="absolute right-[1.5rem] top-[580px] w-[250px] h-[64px]
                      bg-[#0d1631]/80 backdrop-blur-sm rounded-xl
                      transition-opacity duration-300
                      transform translate-y-[104px]" />

      {/* Speed display */}
      <div className="absolute right-[1.5rem] top-[580px] w-[250px] h-[64px]
                      bg-gradient-to-br from-[#0f1423] via-[#151b2e] to-[#0f1423]
                      backdrop-blur-xl backdrop-saturate-150
                      border border-blue-400/20
                      shadow-[rgba(0,0,0,0.56)_0px_22px_70px_4px]
                      rounded-xl
                      flex items-center justify-between py-[0.7rem] px-4
                      transform translate-y-[104px]">
        <div className="flex items-center gap-3">
          <span className="text-4xl font-medium whitespace-nowrap
                          text-[#e5e5e5] leading-none
                          drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]">
            {speedData?.Over || currentBall.over} - {speedData?.Ball_Speed || currentBall.speed}
          </span>
        </div>
      </div>
    </div>
  );
};

export default SpeedBox;
