import React, { useState } from 'react';

const SpeedBox = ({ speedData }) => {
  const [currentBall, setCurrentBall] = useState({
    runs: '0'
  });

  return (
    <div className="fixed inset-0 z-50">
      {/* Semi-transparent backdrop */}
      <div className="absolute right-[1.5rem] top-[580px] w-[100px] h-[52px]
                      bg-[#0d1631]/80 backdrop-blur-sm rounded-lg
                      transition-opacity duration-300" />

      {/* Speed display */}
      <div className="absolute right-[1.5rem] top-[580px] 
                      px-3 py-2 rounded-lg
                      bg-gradient-to-br from-[#0f1423]/98 to-[#151b2e]/98
                      backdrop-blur-xl backdrop-saturate-150
                      border border-blue-400/20
                      flex items-center justify-center
                      transition-all duration-500 ease-in-out">
        <span className="text-4xl font-medium whitespace-nowrap
                        text-[#e5e5e5] leading-none
                        drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]">
          {speedData?.Runs || currentBall.runs}r
        </span>
      </div>
    </div>
  );
};

export default SpeedBox;
