import React, { useState, useEffect } from 'react';

const SpeedBox = ({ speedData }) => {
  // Helper functions - moved to top
  const getSpeedNumber = (speedString) => {
    return parseInt(speedString?.replace('kmph', '') || '0');
  };

  const getSpeedColor = (speed) => {
    const speedNum = getSpeedNumber(speed);
    if (speedNum >= 140) return '#FF0000';
    if (speedNum >= 130) return '#FF6B00';
    if (speedNum >= 120) return '#FFA500';
    return '#FFD700';
  };

  const calculateNeedleAngle = (speed) => {
    const speedNum = getSpeedNumber(speed);
    return -90 + (speedNum * 180 / 160);
  };

  // States - moved after helper functions
  const [currentBall, setCurrentBall] = useState({
    over: '-',
    speed: '150kph'
  });
  const [animationProgress, setAnimationProgress] = useState(0);
  const [speedTrend, setSpeedTrend] = useState('');
  const [displaySpeed, setDisplaySpeed] = useState('0');
  const [needleAngle, setNeedleAngle] = useState(-90);
  const [currentSpeedColor, setCurrentSpeedColor] = useState(getSpeedColor('150kph'));

  // Get current speed value - Move this before the effects
  const speed = speedData?.Ball_Speed || currentBall.speed;

  // Effects
  useEffect(() => {
    if (speedData && speedData[0]) {
      setCurrentBall({
        over: speedData[0].Over || '2.6',
        speed: speedData[0].Ball_Speed || '132kmph'
      });
    }
  }, [speedData]);

  // Smooth animation effect for speed
  useEffect(() => {
    const targetSpeed = getSpeedNumber(speed);
    const startTime = Date.now();
    const duration = 3000;
    const startColor = currentSpeedColor;
    const targetColor = getSpeedColor(speed);

    // Helper function to interpolate between colors
    const interpolateColor = (color1, color2, factor) => {
      // Convert hex to RGB
      const hex2rgb = (hex) => {
        const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
        return result ? [
          parseInt(result[1], 16),
          parseInt(result[2], 16),
          parseInt(result[3], 16)
        ] : null;
      };

      const rgb2hex = (r, g, b) => '#' + [r, g, b]
        .map(x => Math.round(x).toString(16).padStart(2, '0'))
        .join('');

      const c1 = hex2rgb(color1);
      const c2 = hex2rgb(color2);

      if (!c1 || !c2) return color2;

      const r = c1[0] + (c2[0] - c1[0]) * factor;
      const g = c1[1] + (c2[1] - c1[1]) * factor;
      const b = c1[2] + (c2[2] - c1[2]) * factor;

      return rgb2hex(r, g, b);
    };

    const animateSpeed = () => {
      const currentTime = Date.now();
      const elapsed = currentTime - startTime;
      const progress = Math.min(elapsed / duration, 1);

      const easeProgress = 1 - Math.pow(1 - progress, 4);

      // Animate speed number
      const currentSpeed = Math.round(targetSpeed * easeProgress);
      setDisplaySpeed(currentSpeed.toString());

      // Animate needle
      const startAngle = -90;
      const targetAngle = calculateNeedleAngle(speed);
      const currentAngle = startAngle + (targetAngle - startAngle) * easeProgress;
      setNeedleAngle(currentAngle);

      // Animate color
      const interpolatedColor = interpolateColor(startColor, targetColor, easeProgress);
      setCurrentSpeedColor(interpolatedColor);

      if (progress < 1) {
        requestAnimationFrame(animateSpeed);
      }
    };

    requestAnimationFrame(animateSpeed);
  }, [speed]);

  return (
    <div className="fixed inset-0 z-50">
      {/* Update the backdrop with more premium glass effect */}
      <div className="absolute right-[1.5rem] top-[580px] w-[320px] h-[64px]
                      bg-[#0d1631]/60 backdrop-blur-md rounded-xl
                      transition-opacity duration-300
                      transform translate-y-[104px]
                      before:absolute before:inset-0 before:rounded-xl
                      before:bg-gradient-to-b before:from-white/10 before:to-transparent" />

      {/* Speed display container with enhanced gradients */}
      <div className="absolute right-[1.5rem] top-[580px] w-[320px] h-[64px]
                      bg-gradient-to-br from-[#0f1423]/95 via-[#151b2e] to-[#0f1423]/95
                      backdrop-blur-xl backdrop-saturate-150
                      border border-blue-400/30
                      shadow-[0_22px_70px_4px_rgba(0,0,0,0.56),_inset_0_1px_1px_rgba(255,255,255,0.1)]
                      rounded-xl flex items-center gap-2 px-4
                      transform translate-y-[104px] overflow-hidden">

        {/* Over Number in Animated Circle */}
        <div className="relative flex items-center">
          <div className="relative flex">
            {/* Calculate width based on content */}
            <div className="relative w-[calc(1.5ch_+_48px)] h-[48px]">
              {/* Outer rotating ring */}
              <div className="absolute inset-0 rounded-full border-2 border-blue-500/30 
                           animate-[spin_3s_linear_infinite]" />
              {/* Inner pulsing ring */}
              <div className="absolute inset-0 rounded-full border-2 border-blue-400/50
                           animate-[ping_2s_cubic-bezier(0,0,0.2,1)_infinite]" />
              {/* Background circle */}
              <div className="absolute inset-0 rounded-full bg-blue-500/10" />
              {/* Over number */}
              <div className="absolute inset-0 flex items-center justify-center
                           text-3xl font-medium text-[#e5e5e5] leading-none
                           px-3 whitespace-nowrap">
                {speedData?.Over || currentBall.over}
              </div>
            </div>
          </div>
        </div>

        {/* Enhanced Speedometer Arc */}
        <div className="relative w-[80px] h-[40px] flex-shrink-0">
          <svg viewBox="0 0 100 60" className="w-full h-full relative">
            <defs>
              {/* Premium metallic gradient */}
              <linearGradient id="metallic" x1="0%" y1="0%" x2="0%" y2="100%">
                <stop offset="0%" stopColor="rgba(255,255,255,0.7)"/>
                <stop offset="50%" stopColor="rgba(255,255,255,0.2)"/>
                <stop offset="100%" stopColor="rgba(255,255,255,0.5)"/>
              </linearGradient>

              {/* Enhanced glow effect */}
              <filter id="premium-glow">
                <feGaussianBlur stdDeviation="2" result="coloredBlur"/>
                <feComponentTransfer in="coloredBlur">
                  <feFuncA type="linear" slope="0.7"/>
                </feComponentTransfer>
                <feMerge>
                  <feMergeNode in="coloredBlur"/>
                  <feMergeNode in="SourceGraphic"/>
                </feMerge>
              </filter>

              {/* Metallic arc gradient */}
              <linearGradient id="arcGradient" x1="0%" y1="0%" x2="100%" y2="0%">
                <stop offset="0%" stopColor={`${currentSpeedColor}40`}/>
                <stop offset="50%" stopColor={currentSpeedColor}/>
                <stop offset="100%" stopColor={`${currentSpeedColor}40`}/>
              </linearGradient>
            </defs>

            {/* Background ring with subtle gradient */}
            <path
              d="M10 47 A 40 40 0 0 1 90 47"
              fill="none"
              stroke="url(#arcGradient)"
              strokeWidth="6"
              strokeLinecap="round"
              opacity="0.1"
              vectorEffect="non-scaling-stroke"
            />

            {/* Enhanced tick marks */}
            {[...Array(9)].map((_, i) => (
              <g key={i} transform={`rotate(${i * 22.5 - 90}, 50, 45)`}>
                <line
                  x1="50"
                  y1="20"
                  x2="50"
                  y2={i % 2 === 0 ? "23" : "21"}
                  stroke={i % 2 === 0 ? "#6A6A6A" : "#4A4A4A"}
                  strokeWidth={i % 2 === 0 ? "1" : "0.5"}
                  opacity={i % 2 === 0 ? "0.6" : "0.3"}
                  vectorEffect="non-scaling-stroke"
                />
                {i % 2 === 0 && (
                  <text
                    x="50"
                    y="17"
                    fill="#888"
                    fontSize="3"
                    textAnchor="middle"
                    transform={`rotate(${90 - i * 22.5}, 50, 17)`}
                    style={{ fontWeight: 500 }}
                  >
                    {Math.round(i * 20)}
                  </text>
                )}
              </g>
            ))}

            {/* Main colored arc with premium effect */}
            <path
              d="M10 45 A 40 40 0 0 1 90 45"
              fill="none"
              stroke={`url(#arcGradient)`}
              strokeWidth="3"
              strokeLinecap="round"
              vectorEffect="non-scaling-stroke"
              filter="url(#premium-glow)"
              style={{ opacity: 0.9 }}
            />

            {/* Enhanced needle with 3D effect */}
            <g
              style={{
                transform: `rotate(${needleAngle}deg)`,
                transformOrigin: '50px 45px'
              }}
            >
              {/* Needle shadow */}
              <line
                x1="50"
                y1="42"
                x2="50"
                y2="20"
                stroke="rgba(0,0,0,0.5)"
                strokeWidth="3"
                vectorEffect="non-scaling-stroke"
              />
              {/* Needle body */}
              <line
                x1="50"
                y1="42"
                x2="50"
                y2="20"
                stroke={`url(#arcGradient)`}
                strokeWidth="2"
                vectorEffect="non-scaling-stroke"
                filter="url(#premium-glow)"
              />
              {/* Premium needle tip */}
              <polygon
                points="48.5,20 51.5,20 50,16"
                fill={currentSpeedColor}
                filter="url(#premium-glow)"
              />
            </g>

            {/* Enhanced center hub */}
            <circle
              cx="50"
              cy="45"
              r="4"
              fill="#2A2A2A"
              filter="url(#premium-glow)"
            />
            <circle
              cx="50"
              cy="45"
              r="3"
              fill={currentSpeedColor}
              opacity="0.8"
              filter="url(#premium-glow)"
            />
            <circle
              cx="50"
              cy="45"
              r="2"
              fill="url(#metallic)"
              opacity="0.9"
            />
          </svg>
        </div>

        {/* Speed Text with Animation */}
        <div className="flex items-baseline gap-1 ml-auto relative">
          {/* Background glow effect */}
          <div 
            className="absolute inset-0 blur-xl opacity-30"
            style={{ 
              background: `radial-gradient(circle, ${currentSpeedColor} 0%, transparent 70%)`,
              animation: 'pulse 2s infinite'
            }}
          />

          {/* Speed Number */}
          <div className="relative flex items-baseline">
            <span 
              className="text-4xl font-bold relative transform-gpu"
              style={{ 
                color: currentSpeedColor,
                textShadow: `0 0 10px ${currentSpeedColor}`,
                animation: 'textPulseAndScale 3s ease-in-out infinite'
              }}
            >
              {displaySpeed}

              {/* Speed change indicator */}
              {speedTrend && (
                <div 
                  className="absolute -right-3 top-0 text-lg transition-all duration-300"
                  style={{ 
                    color: currentSpeedColor,
                    opacity: speedTrend ? '0.7' : '0',
                    transform: `translateY(${speedTrend === '↑' ? '-2px' : '2px'})`,
                  }}
                >
                  {speedTrend}
                </div>
              )}
            </span>

            {/* Unit (kmph) with floating effect */}
            <span 
              className="text-2xl opacity-70 ml-2 relative"
              style={{ 
                color: currentSpeedColor,
                animation: 'float 3s ease-in-out infinite'
              }}
            >
              kmph
          </span>
          </div>
        </div>

        {/* Updated styles */}
        <style jsx>{`
          @keyframes pulse {
            0% { opacity: 0.2; }
            50% { opacity: 0.4; }
            100% { opacity: 0.2; }
          }

          @keyframes textPulseAndScale {
            0% { 
              opacity: 0.95;
              text-shadow: 0 0 10px currentColor;
              transform: scale(1);
            }
            50% { 
              opacity: 1;
              text-shadow: 0 0 15px currentColor;
              transform: scale(1.03);
            }
            100% { 
              opacity: 0.95;
              text-shadow: 0 0 10px currentColor;
              transform: scale(1);
            }
          }

          @keyframes float {
            0% { transform: translateY(0px); }
            50% { transform: translateY(-3px); }
            100% { transform: translateY(0px); }
          }

          @keyframes premium-pulse {
            0% { opacity: 0.4; filter: brightness(0.8); }
            50% { opacity: 0.8; filter: brightness(1.2); }
            100% { opacity: 0.4; filter: brightness(0.8); }
          }

          @keyframes premium-float {
            0%, 100% { transform: translateY(0) rotate(0deg); }
            50% { transform: translateY(-2px) rotate(0.5deg); }
          }
        `}</style>

        {/* Progress Bar */}
        <div className="absolute left-0 bottom-0 w-full h-1">
          <div 
            className="h-full"
            style={{
              width: `${animationProgress}%`,
              backgroundColor: currentSpeedColor,
              transition: 'all 0.3s ease-out',
              boxShadow: `0 0 10px ${currentSpeedColor}`
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default SpeedBox;
