import React, { useEffect, useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  return (
    <div className="flex flex-col gap-4">
      {/* Main Score Row */}
      <div className="relative w-full h-[85px] flex items-center justify-between
                    bg-gradient-to-r from-[#000033] via-[#000066] to-[#000033]
                    shadow-[0_4px_15px_rgba(0,0,0,0.7)]
                    overflow-hidden">
        <div className="relative z-10 w-full grid grid-cols-[auto_1fr_auto] items-center">
          {/* Left Team */}
          <div className="flex items-center gap-8 relative -top-[2px] shrink-0">
            <div className="flex items-center gap-6 pl-6 relative bottom-[11px] whitespace-nowrap overflow-hidden">
              {team1?.logo && (
                <img 
                  src={team1.logo} 
                  alt={team1.name}
                  className="w-28 h-28 object-contain shrink-0 p-2
                    filter drop-shadow-[0_0_8px_rgba(255,255,255,0.3)]
                    transition-all duration-300 hover:scale-110
                    hover:drop-shadow-[0_0_12px_rgba(255,255,255,0.5)]"
                />
              )}
              <div className="flex items-center gap-6 min-w-0">
                <div className="flex flex-col items-start justify-center min-w-0">
                  <span className="text-8xl font-bold text-[#e5e5e5] leading-none
                                drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]
                                truncate max-w-[400px]">
                    {team1?.name}
                  </span>
                </div>
                <div className="flex items-center gap-4">
                  <span className="text-8xl font-bold text-[#e5e5e5] leading-none relative
                                drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)] whitespace-nowrap">
                    {team1?.score}
                  </span>
                  {team1?.overs && (
                    <span className="text-6xl text-blue-300/80 leading-tight
                                  drop-shadow-[0_0_4px_rgba(147,197,253,0.3)]
                                  relative top-[7px] font-semibold">
                      ({team1.overs})
                    </span>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Middle Event */}
          <div className="flex justify-center items-center w-full min-w-0 px-4">
            {currentEvent?.text && (
              <div className="px-8 py-4 rounded-lg 
                           bg-gradient-to-br from-[#1a1f3c]/90 to-[#2a2f4c]/80
                           border border-blue-400/20
                           shadow-[0_0_15px_rgba(59,130,246,0.15)]
                           min-w-0 max-w-fit overflow-hidden
                           transition-all duration-300 ease-in-out
                           hover:shadow-[0_0_25px_rgba(59,130,246,0.25)]">
                <div className="flex items-center">
                  <span className="text-[2.5rem] font-bold text-[#e5e5e5] leading-none
                                drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]
                                whitespace-nowrap overflow-hidden text-ellipsis">
                    {currentEvent.text}
                  </span>
                </div>
              </div>
            )}
          </div>

          {/* Right Team */}
          <div className="shrink-0">
            <div className="flex items-center justify-end gap-8 relative -top-[2px]">
              <div className="flex items-center gap-6 pr-6 relative bottom-[11px]">
                <span className="text-8xl font-bold tracking-tight leading-none relative bottom-2
                              text-[#e5e5e5] drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]">
                  {team2?.score}
                </span>
                <div className="flex flex-col items-end justify-center gap-1">
                  <span className="text-5xl font-bold text-white leading-none
                                drop-shadow-[0_0_8px_rgba(255,255,255,0.2)]">
                    {team2?.name}
                  </span>
                  <span className="text-2xl text-blue-300/80 leading-tight
                                drop-shadow-[0_0_4px_rgba(147,197,253,0.3)]">
                    ({team2?.overs})
                  </span>
                </div>
                <div className="w-28 h-28 relative">
                  {team2?.logo && (
                    <img 
                      src={team2.logo} 
                      alt={team2.name}
                      className="w-full h-full object-contain p-2
                        filter drop-shadow-[0_0_8px_rgba(255,255,255,0.3)]
                        transition-all duration-300 hover:scale-110
                        hover:drop-shadow-[0_0_12px_rgba(255,255,255,0.5)]"
                    />
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Additional Info Box - Centered below */}
      {additionalInfo && (
        <div className="flex justify-center">
          <AdditionalInfoBox additionalInfo={additionalInfo} />
        </div>
      )}
    </div>
  );
};

export default ScoreboardRow;
