import React from 'react';
import { WavyBackground } from '../../../components/ui/wavy-background';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  return (
    <div className="relative">
      {/* Wavy Background Layer */}
      <div className="absolute inset-0 z-10">
        <WavyBackground
          colors={[
            "rgba(56, 189, 248, 0.3)",  // Sky blue
            "rgba(129, 140, 248, 0.3)",  // Indigo
            "rgba(192, 132, 252, 0.3)",  // Purple
            "rgba(34, 211, 238, 0.3)",  // Cyan
            "rgba(96, 165, 250, 0.3)"   // Blue
          ]}
          waveWidth={100}
          backgroundFill="transparent"
          blur={0}
          speed="slow"
          waveOpacity={0.2}
          className="absolute inset-0"
          containerClassName="w-full h-full"
        />
      </div>

      {/* Content Layer */}
      <div className="relative z-20">
        <div className="flex flex-col gap-4">
          {/* Main Score Row */}
          <div className="w-full flex items-center justify-between px-8 py-4
                      animated-content-box premium-box-shadow content-box-glow
                      rounded-lg border border-blue-400/20
                      backdrop-blur-sm bg-[rgba(17,24,39,0.4)]">
            {/* Left Team */}
            <TeamScoreBox 
              teamName={team1.name}
              score={team1.score}
              overs={team1.overs}
              logo={team1.logo}
            />

            {/* Middle Event */}
            <EventBox event={currentEvent} />

            {/* Right Team */}
            <TeamScoreBox 
              teamName={team2.name}
              score={team2.score}
              overs={team2.overs}
              logo={team2.logo}
              isReversed={true}
            />
          </div>

          {/* Additional Info Box - Centered below */}
          {additionalInfo && (
            <div className="flex justify-center">
              <AdditionalInfoBox additionalInfo={additionalInfo} />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ScoreboardRow;
