import { cn } from "../../lib/utils";
import React from "react";
import { motion } from "framer-motion";
import HoverBorderGradient from "./hover-border-gradient";

export const BackgroundGradient = ({
  children,
  className,
  containerClassName,
  animate = true,
  gradientColors = {
    start: "#3b82f6",
    middle: "#60a5fa",
    end: "#93c5fd",
    base: "#1d4ed8"
  }
}) => {
  return (
    <HoverBorderGradient
      containerClassName={containerClassName}
      gradientColors={{
        start: gradientColors.start,
        end: gradientColors.base
      }}
    >
      <div className={cn("relative z-10 rounded-[11px]", className)}>
        {/* Content container with inner shadow */}
        <div className={cn(
          "relative rounded-[10px] bg-gradient-to-b from-black/90 to-black/80",
          "shadow-[inset_0_1px_1px_rgba(255,255,255,0.1),inset_0_-1px_1px_rgba(0,0,0,0.8)]"
        )}>
          {children}
        </div>

        {/* 3D effect layers */}
        <div className="absolute inset-0 rounded-[10px] bg-gradient-to-b from-white/5 to-transparent -z-[1]" />
        <div className="absolute inset-0 rounded-[10px] bg-gradient-to-t from-black/20 to-transparent -z-[1]" />
      </div>
    </HoverBorderGradient>
  );
};
