import React from 'react';
import { WavyBackground } from '../../ui/wavy-background.jsx';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  return (
    <WavyBackground
      colors={[
        "#38bdf8",  // Sky blue
        "#818cf8",  // Indigo
        "#c084fc",  // Purple
        "#22d3ee",  // Cyan
        "#60a5fa"   // Blue
      ]}
      waveWidth={50}
      backgroundFill="rgba(17, 24, 39, 0.8)"
      blur={2}
      speed="slow"
      waveOpacity={0.3}
      className="rounded-lg overflow-hidden"
      containerClassName="w-full"
    >
      <div className="flex flex-col gap-4 relative z-10">
        {/* Main Score Row */}
        <div className="w-full flex items-center justify-between px-8 py-4
                    animated-content-box premium-box-shadow content-box-glow
                    rounded-lg border border-blue-400/20
                    backdrop-blur-sm bg-black/20">
          {/* Left Team */}
          <TeamScoreBox 
            teamName={team1.name}
            score={team1.score}
            overs={team1.overs}
            logo={team1.logo}
          />

          {/* Middle Event */}
          <EventBox event={currentEvent} />

          {/* Right Team */}
          <TeamScoreBox 
            teamName={team2.name}
            score={team2.score}
            overs={team2.overs}
            logo={team2.logo}
            isReversed={true}
          />
        </div>

        {/* Additional Info Box - Centered below */}
        {additionalInfo && (
          <div className="flex justify-center">
            <AdditionalInfoBox additionalInfo={additionalInfo} />
          </div>
        )}
      </div>
    </WavyBackground>
  );
};

export default ScoreboardRow;
