import React, { useState, useEffect } from 'react';

const SpeedBox = ({ speedData }) => {
  const [currentBall, setCurrentBall] = useState({
    runs: '0'
  });

  useEffect(() => {
    if (!speedData || (Array.isArray(speedData) && speedData.length === 0)) {
      return;
    }

    if (Array.isArray(speedData)) {
      const latestBall = speedData[0];
      if (latestBall) {
        setCurrentBall({
          runs: latestBall.Runs || '0'
        });
      }
    } 
    else if (typeof speedData === 'object') {
      setCurrentBall({
        runs: speedData.Runs || '0'
      });
    }
  }, [speedData]);

  return (
    <div className="flex-1 relative group min-w-fit
                    bg-gradient-to-br from-[#0f1423] via-[#151b2e] to-[#0f1423]
                    backdrop-blur-xl backdrop-saturate-150
                    border border-blue-400/20
                    shadow-[rgba(0,0,0,0.56)_0px_22px_70px_4px]
                    rounded-xl">
      <div className="flex items-center justify-between py-[0.7rem] px-4 relative w-full">
        <span className="text-[1.6rem] leading-[1.5rem] font-normal text-[#e5e5e5]">
          {currentBall.runs}r
        </span>
      </div>
    </div>
  );
};

export default SpeedBox;
