import React, { useState, useEffect } from 'react';

const SpeedBox = ({ speedData }) => {
  const [currentBall, setCurrentBall] = useState({
    over: '5',
    speed: '132kmph'
  });

  // Animation state
  const [animationProgress, setAnimationProgress] = useState(0);

  // Trigger animation when speed changes
  useEffect(() => {
    if (speedData?.Ball_Speed) {
      setAnimationProgress(0);
      const timer = setInterval(() => {
        setAnimationProgress(prev => {
          if (prev >= 100) {
            clearInterval(timer);
            return 100;
          }
          return prev + 5;
        });
      }, 20);

      return () => clearInterval(timer);
    }
  }, [speedData?.Ball_Speed]);

  return (
    <div className="fixed inset-0 z-50">
      {/* Semi-transparent backdrop */}
      <div className="absolute right-[1.5rem] top-[580px] w-[250px] h-[64px]
                      bg-[#0d1631]/80 backdrop-blur-sm rounded-xl
                      transition-opacity duration-300
                      transform translate-y-[104px]" />

      {/* Speed display */}
      <div className="absolute right-[1.5rem] top-[580px] w-[250px] h-[64px]
                      bg-gradient-to-br from-[#0f1423] via-[#151b2e] to-[#0f1423]
                      backdrop-blur-xl backdrop-saturate-150
                      border border-blue-400/20
                      shadow-[rgba(0,0,0,0.56)_0px_22px_70px_4px]
                      rounded-xl
                      flex items-center justify-between py-[0.7rem] px-4
                      transform translate-y-[104px]
                      overflow-hidden">

        {/* Speedometer Animation */}
        <div className="absolute left-0 bottom-0 w-full h-1 bg-gray-800">
          <div 
            className="h-full bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500"
            style={{
              width: `${animationProgress}%`,
              transition: 'width 0.3s ease-out'
            }}
          />
        </div>

        {/* Speed Icon */}
        <div className="flex items-center gap-3">
          <svg 
            viewBox="0 0 24 24" 
            className={`w-8 h-8 ${
              animationProgress < 100 ? 'animate-spin' : ''
            } text-blue-400`}
            fill="none" 
            stroke="currentColor"
          >
            <path 
              strokeLinecap="round" 
              strokeLinejoin="round" 
              strokeWidth={1.5}
              d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"
            />
          </svg>

          {/* Speed Text */}
          <span className="text-4xl font-medium whitespace-nowrap
                          text-[#e5e5e5] leading-none
                          drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]">
            {speedData?.Over || currentBall.over} - {speedData?.Ball_Speed || currentBall.speed}
          </span>
        </div>
      </div>
    </div>
  );
};

export default SpeedBox;
