import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import ScoreRight from './ScoreRight';

const MainScoreDisplay = ({ cricketData }) => {
  const [mouseEnter, setMouseEnter] = useState(false);
  const stars = 54;
  const columns = 9;
  const [glowingStars, setGlowingStars] = useState([]);
  const highlightedStars = useRef([]);

  useEffect(() => {
    const interval = setInterval(() => {
      highlightedStars.current = Array.from({ length: 3 }, () =>
        Math.floor(Math.random() * stars)
      );
      setGlowingStars([...highlightedStars.current]);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div 
      className="grid grid-cols-[auto_1fr_auto] items-center gap-4 shadow-[rgba(0,0,0,0.56)_0px_22px_70px_4px] border border-[rgb(28,31,63)] relative overflow-hidden"
      onMouseEnter={() => setMouseEnter(true)}
      onMouseLeave={() => setMouseEnter(false)}
    >
      {/* Add the stars background */}
      <div className="absolute inset-0 z-0"
           style={{
             display: "grid",
             gridTemplateColumns: `repeat(${columns}, 1fr)`,
             gap: `1px`,
             pointerEvents: 'none',
             opacity: 0.3
           }}>
        {[...Array(stars)].map((_, starIdx) => {
          const isGlowing = glowingStars.includes(starIdx);
          const delay = (starIdx % 10) * 0.1;
          const staticDelay = starIdx * 0.01;
          return (
            <div
              key={`star-${starIdx}`}
              className="relative flex items-center justify-center"
            >
              <motion.div
                initial={{ scale: 1 }}
                animate={{
                  scale: mouseEnter || isGlowing ? [1, 1.2, 2.5, 2.2, 1.5] : 1,
                  background: mouseEnter || isGlowing ? "#fff" : "#666",
                }}
                transition={{
                  duration: 2,
                  ease: "easeInOut",
                  delay: mouseEnter ? staticDelay : delay,
                }}
                className="bg-[#666] h-[1px] w-[1px] rounded-full relative z-20"
              />
              {(mouseEnter || isGlowing) && (
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{
                    duration: 2,
                    ease: "easeInOut",
                    delay: mouseEnter ? staticDelay : delay,
                  }}
                  exit={{ opacity: 0 }}
                  className="absolute left-1/2 -translate-x-1/2 z-10 h-[4px] w-[4px] 
                           rounded-full bg-blue-500 blur-[1px] shadow-2xl shadow-blue-400"
                />
              )}
            </div>
          );
        })}
      </div>

      {/* Left Team Score */}
      <div className="flex items-center gap-8 relative -top-[2px] shrink-0 z-10">
        <div className="flex items-center gap-6 pl-6 relative bottom-[11px] whitespace-nowrap overflow-hidden">
          {cricketData?.teams?.team1?.logo && (
            <img 
              src={cricketData.teams.team1.logo} 
              alt={cricketData.teams.team1.name || ''}
              className="w-28 h-28 object-contain shrink-0 p-2
                filter drop-shadow-[0_0_8px_rgba(255,255,255,0.3)]
                transition-all duration-300 hover:scale-110
                hover:drop-shadow-[0_0_12px_rgba(255,255,255,0.5)]"
            />
          )}
          <div className="flex items-center gap-6 min-w-0">
            <div className="flex flex-col items-start justify-center min-w-0">
              <span className="text-8xl font-bold text-[#e5e5e5] leading-none
                            drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]
                            truncate max-w-[400px]">
                {cricketData?.teams?.team1?.name || ''}
              </span>
            </div>
            <div className="flex items-center gap-4">
              <span className="text-8xl font-bold text-[#e5e5e5] leading-none relative
                            drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)] whitespace-nowrap">
                {cricketData?.teams?.team1?.score || ''}
              </span>
              {cricketData?.teams?.team1?.overs && (
                <span className="text-6xl text-blue-300/80 leading-tight
                              drop-shadow-[0_0_4px_rgba(147,197,253,0.3)]
                              relative top-[7px] font-semibold">
                  ({cricketData.teams.team1.overs})
                </span>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Middle Event */}
      <div className="flex justify-center items-center w-full min-w-0 px-4 z-10">
        {cricketData.currentEvent?.additionalInfo && (
          <div className="px-8 py-4 rounded-lg 
                       bg-gradient-to-br from-[#1a1f3c]/90 to-[#2a2f4c]/80
                       border border-blue-400/20
                       shadow-[0_0_15px_rgba(59,130,246,0.15)]
                       min-w-0 max-w-fit
                       overflow-hidden
                       transition-all duration-300 ease-in-out
                       hover:shadow-[0_0_25px_rgba(59,130,246,0.25)]">
            <div className="flex items-center">
              <span className="text-[2.5rem] font-bold
                            text-[#e5e5e5] leading-none
                            drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]
                            whitespace-nowrap overflow-hidden text-ellipsis
                            rtl:text-right">
                {cricketData.currentEvent.additionalInfo}
              </span>
            </div>
          </div>
        )}
      </div>

      {/* Right Team Score */}
      <div className="shrink-0 z-10">
        <ScoreRight />
      </div>
    </div>
  );
};

export default MainScoreDisplay;
