import React from 'react';
import { BackgroundGradientAnimation } from '../../ui/background-gradient-animation';

const ScoreboardRow = ({ children, className }) => {
  return (
    <BackgroundGradientAnimation
      gradientBackgroundStart="rgb(20, 20, 40)" // Dark blue background start
      gradientBackgroundEnd="rgb(0, 17, 82)"    // Dark blue background end
      firstColor="18, 113, 255"  // Bright blue
      secondColor="59, 130, 246" // Medium blue
      thirdColor="147, 197, 253" // Light blue
      fourthColor="59, 130, 246" // Medium blue
      fifthColor="18, 113, 255"  // Bright blue
      pointerColor="59, 130, 246" // Interactive color
      size="100%"
      blendingValue="overlay"
      className={className}
      containerClassName="w-full relative"
      interactive={true}
    >
      <div className="p-4">
        <div className="w-full flex items-center justify-between px-8 py-4
                    animated-content-box premium-box-shadow content-box-glow
                    rounded-lg border border-blue-400/20
                    backdrop-blur-md bg-black/20">
          {children}
        </div>
      </div>
    </BackgroundGradientAnimation>
  );
};

export default ScoreboardRow;
