import React from 'react';
import { WavyBackground } from '../../ui/wavy-background';

const ScoreboardRow = ({ data, isActive }) => {
  return (
    <div className="relative w-full h-24 overflow-hidden rounded-lg bg-[#0f1631]/95">
      <WavyBackground 
        colors={[
          'rgba(59, 130, 246, 0.8)',  // Increased opacity
          'rgba(99, 102, 241, 0.8)',
          'rgba(139, 92, 246, 0.8)',
        ]}
        blur={2}
        speed="slow"
        waveOpacity={0.6}  // Increased from 0.3
        waveWidth={200}    // Increased from 150
        containerClassName="!h-full !w-full absolute inset-0"
        className="!h-full !w-full"
        backgroundFill="rgba(15, 22, 49, 0.9)"  // Darker background
      >
        <div className="relative z-10 flex items-center justify-between p-4 h-full backdrop-blur-[2px]">
          <div className="flex items-center space-x-4">
            {data.teamLogo && (
              <img 
                src={data.teamLogo} 
                alt={data.teamName} 
                className="w-8 h-8 object-contain"
              />
            )}
            <span className="text-lg font-semibold text-white">
              {data.teamName}
            </span>
          </div>
          <div className="flex items-center space-x-4">
            <span className="text-lg font-bold text-white">
              {data.score}
            </span>
            {data.overs && (
              <span className="text-sm text-gray-300">
                ({data.overs})
              </span>
            )}
          </div>
        </div>
      </WavyBackground>
    </div>
  );
};

export default ScoreboardRow;
