import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const formatPlayerName = (fullName) => {
  if (!fullName) return '';
  const nameParts = fullName.split(' ');
  if (nameParts.length <= 2) return fullName;
  return `${nameParts[0]} ${nameParts[nameParts.length - 1]}`;
};

const borderVariants = {
  animate: {
    borderColor: ['rgba(239, 68, 68, 0.2)', 'rgba(239, 68, 68, 0.4)', 'rgba(239, 68, 68, 0.2)'],
    boxShadow: [
      '0 0 0px rgba(239, 68, 68, 0)',
      '0 0 15px rgba(239, 68, 68, 0.3)',
      '0 0 0px rgba(239, 68, 68, 0)'
    ],
  }
};

const BowlerCard = ({ bowler, isAnimated }) => {
  const [imageLoaded, setImageLoaded] = useState(false);
  const [shouldSweep, setShouldSweep] = useState(false);
  const [shouldCelebrate, setShouldCelebrate] = useState(false);
  const [autoHover, setAutoHover] = useState(false);
  const prevStatsRef = useRef({ wickets: bowler?.wickets, maidens: bowler?.maidens });
  const animationLockRef = useRef(false);
  const prevBowlerName = useRef(bowler?.name);
  const [isValueChanged, setIsValueChanged] = useState(false);
  const prevWickets = useRef(bowler?.wickets);
  const prevRuns = useRef(bowler?.runs);

  // Track bowler changes
  useEffect(() => {
    if (bowler?.name && bowler.name !== prevBowlerName.current) {
      prevBowlerName.current = bowler.name;
      setAutoHover(true);
      const timer = setTimeout(() => {
        setAutoHover(false);
      }, 3000); // 3 seconds of hover effect
      return () => clearTimeout(timer);
    }
  }, [bowler?.name]);

  // Handle bowler changes with sweep animation
  useEffect(() => {
    if (bowler?.name && !animationLockRef.current) {
      setShouldSweep(true);
      setTimeout(() => setShouldSweep(false), 800);
    }
  }, [bowler?.name]);

  // Handle special events with celebration animation
  useEffect(() => {
    if (!animationLockRef.current && bowler) {
      const hasNewWicket = bowler.wickets > (prevStatsRef.current.wickets || 0);
      const hasNewMaiden = bowler.maidens > (prevStatsRef.current.maidens || 0);
      // Add hat-trick logic here if available in your data

      if (hasNewWicket || hasNewMaiden) {
        animationLockRef.current = true;
        setShouldCelebrate(true);

        const card = document.querySelector(`[data-bowler-id="${bowler.name}"]`);
        if (card) {
          card.classList.add('animate-smooth-hover');

          setTimeout(() => {
            card.classList.remove('animate-smooth-hover');
            setShouldCelebrate(false);
            animationLockRef.current = false;
          }, 2500);
        }
      }

      prevStatsRef.current = {
        wickets: bowler.wickets,
        maidens: bowler.maidens
      };
    }
  }, [bowler?.wickets, bowler?.maidens]);

  // Add new useEffect for milestone triggers
  useEffect(() => {
    if (!animationLockRef.current && bowler) {
      const hasNewWicket = bowler.wickets > (prevStatsRef.current.wickets || 0);
      const hasNewMaiden = bowler.maidens > (prevStatsRef.current.maidens || 0);

      // Check for hat-trick (if data available)
      const hasHatTrick = false; // Implement your hat-trick logic here

      if (hasNewWicket || hasNewMaiden || hasHatTrick) {
        animationLockRef.current = true;
        setAutoHover(true);

        // Reset after animation duration
        setTimeout(() => {
          setAutoHover(false);
          animationLockRef.current = false;
        }, 300); // Match CSS transition duration
      }

      prevStatsRef.current = {
        wickets: bowler.wickets,
        maidens: bowler.maidens
      };
    }
  }, [bowler?.wickets, bowler?.maidens]);

  // Add value change detection
  useEffect(() => {
    if (bowler?.wickets !== prevWickets.current || 
        bowler?.runs !== prevRuns.current) {
      setIsValueChanged(true);
      const timer = setTimeout(() => setIsValueChanged(false), 600);

      prevWickets.current = bowler?.wickets;
      prevRuns.current = bowler?.runs;

      return () => clearTimeout(timer);
    }
  }, [bowler?.wickets, bowler?.runs]);

  return (
    <motion.div 
      className={`glow-box p-0 flex flex-col premium-box-shadow content-box-glow
                ${autoHover ? 'auto-hover' : ''} 
                group animated-content-box relative
                border border-red-400/20
                overflow-hidden`}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
    >
      <motion.div
        className="absolute inset-0 pointer-events-none"
        animate="animate"
        variants={borderVariants}
        transition={{
          duration: 3,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />

      <motion.div
        className="absolute inset-0 pointer-events-none"
        animate={{
          background: [
            'linear-gradient(90deg, transparent 0%, rgba(239,68,68,0.1) 50%, transparent 100%)',
            'linear-gradient(90deg, transparent 100%, rgba(239,68,68,0.1) 50%, transparent 0%)'
          ],
        }}
        transition={{
          duration: 2,
          repeat: Infinity,
          repeatType: "reverse",
          ease: "linear"
        }}
      />

      <motion.div
        className="absolute inset-0 pointer-events-none"
        initial={{ opacity: 0 }}
        animate={{
          opacity: [0, 1, 0],
          background: `
            linear-gradient(45deg, transparent 95%, rgba(239,68,68,0.3)) top left,
            linear-gradient(-45deg, transparent 95%, rgba(239,68,68,0.3)) top right,
            linear-gradient(135deg, transparent 95%, rgba(239,68,68,0.3)) bottom left,
            linear-gradient(-135deg, transparent 95%, rgba(239,68,68,0.3)) bottom right
          `,
          backgroundSize: '50% 50%',
          backgroundRepeat: 'no-repeat'
        }}
        transition={{
          duration: 2,
          repeat: Infinity,
          repeatType: "reverse",
          ease: "easeInOut"
        }}
      />

      <div className="px-8 pt-4 pb-0 flex-1 relative overflow-hidden">
        {/* Animated gradient overlay */}
        <div className="absolute inset-0 bg-gradient-to-r from-red-500/0 via-red-400/5 to-red-500/0 
                      translate-x-[-200%] group-hover:translate-x-[200%] 
                      transition-transform duration-1000 ease-in-out"></div>

        <div className="flex items-center justify-between gap-6 relative">
          <div className="relative">
            {/* Premium backdrop glow based on player type */}
            <div className="absolute -inset-[2px] z-0 
                            bg-gradient-to-r from-red-500/20 via-red-400/10 to-red-500/20
                            blur-md rounded-t-lg"></div>

            <div className={`relative w-[12.5rem] h-50 overflow-hidden
                          rounded-t-[10px]
                          border-t border-l border-r border-[#4169e1]
                          shadow-[0_0_15px_rgba(239,68,68,0.5)]
                          group-hover:shadow-[0_0_25px_rgba(239,68,68,0.6)]
                          transform hover:scale-105 transition-all duration-500
                          before:absolute before:inset-0 before:z-10 
                          before:bg-gradient-to-t before:from-black/20 before:to-transparent
                          after:absolute after:inset-0 after:z-20 
                          after:bg-gradient-to-b after:from-white/10 after:via-transparent after:to-black/30`}>
              {/* Ambient light effect */}
              <div className="absolute inset-0 z-15
                              bg-[radial-gradient(circle_at_50%_0%,rgba(239,68,68,0.15),transparent_70%)]">
              </div>

              {/* Edge highlight */}
              <div className="absolute inset-0 z-20 
                              bg-gradient-to-r from-white/5 via-transparent to-white/5"></div>

              {/* Premium overlay */}
              <div className="absolute inset-0 z-25
                              bg-gradient-to-b from-red-400/10 via-transparent to-red-900/20
                              opacity-60"></div>

              {bowler?.image && (
                <img 
                  src={bowler.image}
                  alt={bowler.name || ''}
                  className={`w-full h-full transition-all duration-700
                    ${imageLoaded ? 'opacity-100 scale-100' : 'opacity-0 scale-105'}
                    motion-safe:transform motion-safe:backface-hidden
                    relative top-[7px]`}
                  style={{ 
                    imageRendering: 'high-quality',
                    objectFit: 'cover',
                    objectPosition: 'center top',
                    filter: 'contrast(1.1) saturate(1.1) brightness(1.05)',
                    transform: 'perspective(1000px) rotateY(0deg) scale(1.02)',
                  }}
                  onLoad={() => setImageLoaded(true)}
                  onError={(e) => {
                    e.target.style.display = 'none';
                  }}
                />
              )}

              {/* Enhanced shine effect */}
              <div className="absolute inset-0 z-30 
                              bg-gradient-to-tr from-transparent via-white/10 to-transparent 
                              opacity-0 group-hover:opacity-100 
                              transition-opacity duration-700 
                              animate-[shine_2s_infinite]">
              </div>
            </div>
          </div>
          <div className="text-right flex-1">
            <motion.div 
              className="text-[2.5rem] font-bold mb-4 text-right"
              whileHover={{ scale: 1.02 }}
              transition={{ type: "spring", stiffness: 400, damping: 10 }}
            >
              {bowler?.name}
            </motion.div>

            <AnimatePresence mode="wait">
              <motion.div 
                key={`${bowler?.wickets}-${bowler?.runs}`}
                className="text-[3.6rem] font-bold text-[#e5e5e5] leading-none relative"
                initial={{ opacity: 0, y: 10 }}
                animate={{ 
                  opacity: 1, 
                  y: 0,
                  scale: isValueChanged ? [1, 1.08, 1] : 1,
                  transition: {
                    scale: {
                      duration: 0.4,
                      ease: [0.34, 1.56, 0.64, 1] // Custom spring-like easing
                    },
                    opacity: { duration: 0.2 },
                    y: { duration: 0.2 }
                  }
                }}
                exit={{ opacity: 0, y: -10, transition: { duration: 0.2 } }}
              >
                <motion.span
                  animate={isValueChanged ? {
                    color: ['#e5e5e5', '#ef4444', '#e5e5e5'],
                    textShadow: [
                      '0 0 0px rgba(239,68,68,0)',
                      '0 0 20px rgba(239,68,68,0.5)',
                      '0 0 0px rgba(239,68,68,0)'
                    ]
                  } : {}}
                  transition={{
                    duration: 0.6,
                    ease: 'easeOut'
                  }}
                >
                  {bowler?.wickets}-{bowler?.runs}
                </motion.span>{' '}
                <motion.span 
                  className="text-[2.4rem] text-gray-400"
                  animate={isValueChanged ? {
                    color: ['#9ca3af', '#ef4444', '#9ca3af'],
                    textShadow: [
                      '0 0 0px rgba(239,68,68,0)',
                      '0 0 15px rgba(239,68,68,0.3)',
                      '0 0 0px rgba(239,68,68,0)'
                    ]
                  } : {}}
                  transition={{
                    duration: 0.6,
                    ease: 'easeOut'
                  }}
                >
                  ({bowler?.overs})
                </motion.span>
              </motion.div>
            </AnimatePresence>

            {/* Enhanced value change flash effect */}
            <AnimatePresence>
              {isValueChanged && (
                <>
                  <motion.div
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={{ 
                      opacity: [0.15, 0],
                      scale: [0.8, 1.5],
                      background: [
                        'rgba(239,68,68,0.2)',
                        'rgba(239,68,68,0)'
                      ]
                    }}
                    exit={{ opacity: 0 }}
                    transition={{ 
                      duration: 0.8,
                      ease: 'easeOut'
                    }}
                    className="absolute inset-0 rounded-xl"
                  />
                  <motion.div
                    initial={{ opacity: 0, scale: 1 }}
                    animate={{ 
                      opacity: [0.2, 0],
                      scale: [1, 1.2],
                    }}
                    transition={{ 
                      duration: 0.5,
                      ease: 'easeOut'
                    }}
                    className="absolute inset-0 bg-gradient-to-r from-red-400/10 to-red-500/10 rounded-xl"
                    style={{ mixBlendMode: 'overlay' }}
                  />
                </>
              )}
            </AnimatePresence>
          </div>
        </div>
      </div>
      <motion.div 
        className="flex items-center justify-center text-[1.8rem] text-gray-300 
                  bg-gradient-to-r from-[#2a2f4c]/50 via-[#2a2f4c]/60 to-[#2a2f4c]/50
                  px-8 py-4 rounded-b-lg"
        whileHover={{ 
          backgroundColor: 'rgba(42,47,76,0.7)',
          transition: { duration: 0.3 }
        }}
      >
        <AnimatePresence mode="wait">
          <motion.div 
            key={bowler?.economy}
            className="flex items-center gap-2"
            initial={{ opacity: 0, y: 5 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -5 }}
            transition={{ duration: 0.2 }}
          >
            <span className="text-gray-400">E:</span>
            <span className="font-semibold">{bowler?.economy}</span>
          </motion.div>
        </AnimatePresence>
      </motion.div>
    </motion.div>
  );
};

export default BowlerCard;
