import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const SpeedBox = ({ speedData }) => {
  // States
  const [currentBall, setCurrentBall] = useState({
    over: '30.1',
    speed: '130kph'
  });
  const [animationProgress, setAnimationProgress] = useState(0);
  const [speedTrend, setSpeedTrend] = useState('');
  const [displaySpeed, setDisplaySpeed] = useState('0');
  const [needleAngle, setNeedleAngle] = useState(-90);
  const [isVisible, setIsVisible] = useState(false);

  // Helper functions
  const getSpeedNumber = (speedString) => {
    return parseInt(speedString?.replace('kph', '') || '0');
  };

  const getSpeedColor = (speed) => {
    const speedNum = getSpeedNumber(speed);
    if (speedNum >= 140) return '#FF0000';
    if (speedNum >= 130) return '#FF6B00';
    if (speedNum >= 120) return '#FFA500';
    return '#FFD700';
  };

  const calculateNeedleAngle = (speed) => {
    const speedNum = getSpeedNumber(speed);
    return -90 + (speedNum * 180 / 160);
  };

  // Get current speed value - Move this before the effects
  const speed = speedData?.Ball_Speed || currentBall.speed;
  const speedColor = getSpeedColor(speed);

  // Effects
  const isInitialRender = useRef(true);

  useEffect(() => {
    if (speedData && speedData[0]) {
      if (isInitialRender.current) {
        isInitialRender.current = false;
        return;
      }

      setCurrentBall({
        over: speedData[0].Over || '2.6',
        speed: speedData[0].Ball_Speed || '132kph'
      });
    }
  }, [speedData]);

  useEffect(() => {
    if (speedData && speedData[0]?.Ball_Speed !== currentBall.speed) {
      setIsVisible(true);

      const timer = setTimeout(() => {
        setIsVisible(false);
      }, 4000);

      return () => clearTimeout(timer);
    }
  }, [speedData, currentBall.speed]);

  // Smooth animation effect for speed
  useEffect(() => {
    const targetSpeed = getSpeedNumber(speed);
    const startTime = Date.now();
    const duration = 3000;

    const animateSpeed = () => {
      const currentTime = Date.now();
      const elapsed = currentTime - startTime;
      const progress = Math.min(elapsed / duration, 1);

      const easeProgress = 1 - Math.pow(1 - progress, 4);

      // Animate speed number
      const currentSpeed = Math.round(targetSpeed * easeProgress);
      setDisplaySpeed(currentSpeed.toString());

      // Animate needle - sync with speed number animation
      const startAngle = -90;
      const targetAngle = calculateNeedleAngle(speed);
      const currentAngle = startAngle + (targetAngle - startAngle) * easeProgress;
      setNeedleAngle(currentAngle);

      if (progress < 1) {
        requestAnimationFrame(animateSpeed);
      }
    };

    requestAnimationFrame(animateSpeed);
  }, [speed]);

  const variants = {
    initial: { 
      opacity: 0,
      y: -20,
      scale: 0.95
    },
    animate: { 
      opacity: 1,
      y: 104,
      scale: 1,
      transition: {
        type: "spring",
        damping: 20,
        stiffness: 300
      }
    },
    exit: { 
      opacity: 0,
      y: 20,
      scale: 0.95,
      transition: {
        type: "spring",
        damping: 25,
        stiffness: 200,
        duration: 0.5
      }
    }
  };

  return (
    <div className="fixed inset-0 z-20">
      <AnimatePresence mode="wait">
        {isVisible && (
          <>
            {/* Backdrop */}
            <motion.div
              initial="initial"
              animate="animate"
              exit="exit"
              variants={variants}
              className="absolute right-[1.5rem] top-[580px] w-[320px] h-[64px]
                        bg-[#0d1631]/60 backdrop-blur-md rounded-xl
                        before:absolute before:inset-0 before:rounded-xl
                        before:bg-gradient-to-b before:from-white/10 before:to-transparent"
            />

            {/* Main container */}
            <motion.div
              initial="initial"
              animate="animate"
              exit="exit"
              variants={variants}
              className="absolute right-[1.5rem] top-[580px] w-[320px] h-[64px]
                        bg-[#0d1631] border border-blue-400/30 rounded-xl
                        shadow-[0_4px_20px_4px_rgba(0,0,0,0.7)]
                        overflow-hidden"
            >
              {/* Premium glass effect background layers */}
              <div className="absolute inset-0 z-10 overflow-hidden pointer-events-none">
                {/* Animated gradient background */}
                <div className="absolute inset-0 bg-gradient-to-br from-[#1a2442] to-[#2a3454] opacity-95" />

                {/* Premium light effects */}
                <div className="absolute inset-0 bg-gradient-to-b from-white/[0.06] to-transparent" />
                <div className="absolute -inset-[150%] bg-[radial-gradient(circle_at_50%_50%,rgba(30,64,175,0.05),transparent_35%)]
                            animate-[slow-spin_20s_linear_infinite]" />
              </div>

              {/* Main content wrapper */}
              <div className="relative z-20 flex items-center gap-2 px-4 h-full">
                {/* Over Number with enhanced OBS visibility */}
                <div className="relative flex items-center">
                  <div className="relative flex">
                    <div className="relative w-[calc(1.5ch_+_48px)] h-[48px]">
                      {/* Premium ring effects */}
                      <div className="absolute inset-0 rounded-full border-[1.5px] border-blue-400/40
                                    animate-[spin_4s_linear_infinite]" />
                      <div className="absolute inset-0 rounded-full border-[1.5px] border-blue-500/30
                                    animate-[spin_6s_linear_infinite_reverse]" />

                      {/* Enhanced glow effects */}
                      <div className="absolute inset-[-1px] rounded-full bg-blue-500/5
                                    shadow-[inset_0_0_15px_rgba(59,130,246,0.2)]" />

                      {/* Over number with guaranteed OBS visibility */}
                      <div className="absolute inset-0 flex items-center justify-center">
                        <span className="text-3xl font-bold text-[#e5e5e5] leading-none
                                     drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)] whitespace-nowrap">
                          {speedData?.Over || currentBall.over}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Enhanced Speedometer */}
                <div className="relative w-[80px] h-[40px] flex-shrink-0">
                  {/* Add premium glass effect background */}
                  <div className="absolute inset-0 rounded-lg bg-white/5" />

                  {/* Existing speedometer SVG with enhanced colors */}
                  <svg viewBox="0 0 100 60" className="w-full h-full relative">
                    {/* Update the metallic gradient for better visibility */}
                    <linearGradient id="metallic" x1="0%" y1="0%" x2="0%" y2="100%">
                      <stop offset="0%" stopColor="rgba(255,255,255,0.95)"/>
                      <stop offset="50%" stopColor="rgba(160,160,160,0.6)"/>
                      <stop offset="100%" stopColor="rgba(255,255,255,0.8)"/>
                    </linearGradient>

                    {/* Rest of the SVG content... */}
                  </svg>
                </div>

                {/* Speed Display with enhanced OBS visibility */}
                <div className="flex items-baseline gap-1 ml-auto">
                  <span className="text-4xl font-bold text-[#e5e5e5] leading-none
                                drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]">
                    {displaySpeed}
                  </span>
                  <span className="text-2xl text-[#e5e5e5]/70 leading-none ml-2
                                drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]">
                    kph
                  </span>
                </div>
              </div>

              {/* Enhanced progress bar */}
              <div className="absolute left-0 bottom-0 w-full h-1 bg-black/20">
                <div 
                  className="h-full relative"
                  style={{
                    width: `${animationProgress}%`,
                    backgroundColor: speedColor,
                  }}
                >
                  {/* Add glow effect */}
                  <div className="absolute inset-0 blur-[6px] bg-inherit opacity-50" />
                </div>
              </div>
            </motion.div>
          </>
        )}
      </AnimatePresence>
    </div>
  );
};

export default SpeedBox;
