import { cn } from "../../utils/cn";
import React, { useEffect, useRef, useState, useCallback } from "react";

// Star interface
const createStar = (x, y, radius, opacity, twinkleSpeed) => ({
  x,
  y,
  radius,
  opacity,
  twinkleSpeed
});

// Shooting star interface
const createShootingStar = (x, y, angle) => ({
  id: Date.now(),
  x,
  y,
  angle,
  scale: 1,
  speed: Math.random() * 20 + 10, // between 10 and 30
  distance: 0
});

export const BackgroundGradientAnimation = ({
  gradientBackgroundStart = "rgb(13, 22, 49)",
  gradientBackgroundEnd = "rgb(26, 39, 81)",
  firstColor = "59, 130, 246",
  secondColor = "30, 64, 175",
  thirdColor = "17, 24, 39",
  starDensity = 0.00015,
  size = "100%",
  className,
  interactive = false,
  containerClassName,
  children,
}) => {
  const canvasRef = useRef(null);
  const [stars, setStars] = useState([]);
  const [shootingStar, setShootingStar] = useState(null);

  // Generate initial stars
  const generateStars = useCallback((width, height) => {
    const area = width * height;
    const numStars = Math.floor(area * starDensity);
    return Array.from({ length: numStars }, () => createStar(
      Math.random() * width,
      Math.random() * height,
      Math.random() * 0.05 + 0.5,
      Math.random() * 0.5 + 0.5,
      0.5 + Math.random() * 0.5
    ));
  }, [starDensity]);

  // Initialize canvas and stars
  useEffect(() => {
    if (!canvasRef.current) return;
    
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const { width, height } = canvas.getBoundingClientRect();
    
    canvas.width = width;
    canvas.height = height;
    setStars(generateStars(width, height));

    const resizeObserver = new ResizeObserver(() => {
      canvas.width = canvas.getBoundingClientRect().width;
      canvas.height = canvas.getBoundingClientRect().height;
      setStars(generateStars(canvas.width, canvas.height));
    });

    resizeObserver.observe(canvas);
    return () => resizeObserver.disconnect();
  }, [generateStars]);

  // Animate stars and shooting stars
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    let animationFrameId;

    const render = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // Draw stars
      stars.forEach(star => {
        ctx.beginPath();
        ctx.arc(star.x, star.y, star.radius, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(255, 255, 255, ${star.opacity})`;
        ctx.fill();

        // Twinkle effect
        star.opacity = 0.5 + Math.abs(Math.sin(Date.now() * 0.001 / star.twinkleSpeed) * 0.5);
      });

      // Draw shooting star if exists
      if (shootingStar) {
        ctx.save();
        ctx.translate(shootingStar.x, shootingStar.y);
        ctx.rotate((shootingStar.angle * Math.PI) / 180);
        
        const gradient = ctx.createLinearGradient(0, 0, 20 * shootingStar.scale, 0);
        gradient.addColorStop(0, 'rgba(46, 185, 223, 0)');
        gradient.addColorStop(1, 'rgba(158, 0, 255, 1)');
        
        ctx.fillStyle = gradient;
        ctx.fillRect(0, 0, 20 * shootingStar.scale, 2);
        ctx.restore();

        // Update shooting star position
        setShootingStar(prev => {
          if (!prev) return null;
          const newX = prev.x + prev.speed * Math.cos((prev.angle * Math.PI) / 180);
          const newY = prev.y + prev.speed * Math.sin((prev.angle * Math.PI) / 180);
          const newDistance = prev.distance + prev.speed;
          
          if (newX < -20 || newX > canvas.width + 20 || 
              newY < -20 || newY > canvas.height + 20) {
            return null;
          }
          
          return {
            ...prev,
            x: newX,
            y: newY,
            distance: newDistance,
            scale: 1 + newDistance / 100
          };
        });
      }

      animationFrameId = requestAnimationFrame(render);
    };

    render();
    return () => cancelAnimationFrame(animationFrameId);
  }, [stars, shootingStar]);

  // Create new shooting stars periodically
  useEffect(() => {
    const createNewShootingStar = () => {
      const side = Math.floor(Math.random() * 4);
      const offset = Math.random() * window.innerWidth;
      let x, y, angle;

      switch (side) {
        case 0: // Top
          x = offset;
          y = 0;
          angle = 45;
          break;
        case 1: // Right
          x = window.innerWidth;
          y = offset;
          angle = 135;
          break;
        case 2: // Bottom
          x = offset;
          y = window.innerHeight;
          angle = 225;
          break;
        case 3: // Left
          x = 0;
          y = offset;
          angle = 315;
          break;
      }

      setShootingStar(createShootingStar(x, y, angle));
      setTimeout(createNewShootingStar, Math.random() * 3000 + 1200);
    };

    createNewShootingStar();
  }, []);

  return (
    <div className={cn(
      "h-screen w-screen relative overflow-hidden top-0 left-0",
      "bg-[linear-gradient(40deg,var(--gradient-background-start),var(--gradient-background-end))]",
      containerClassName
    )}>
      <canvas
        ref={canvasRef}
        className="absolute inset-0 w-full h-full"
      />
      <div className={cn(
        "absolute inset-0 w-full h-full bg-[radial-gradient(circle_at_center,_rgba(var(--first-color),_0.8)_0,_rgba(var(--first-color),_0)_50%)]",
        "mix-blend-hard-light"
      )} />
      <div className={cn(
        "absolute inset-0 w-full h-full bg-[radial-gradient(circle_at_center,_rgba(var(--second-color),_0.8)_0,_rgba(var(--second-color),_0)_50%)]",
        "mix-blend-hard-light"
      )} />
      <div className={cn(
        "absolute inset-0 w-full h-full bg-[radial-gradient(circle_at_center,_rgba(var(--third-color),_0.8)_0,_rgba(var(--third-color),_0)_50%)]",
        "mix-blend-hard-light"
      )} />
      {children}
    </div>
  );
};
