import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const InfoBox = ({ label, value, show = true }) => {
  const [prevValue, setPrevValue] = useState(value);
  const [isValueChanged, setIsValueChanged] = useState(false);

  // Track value changes for animation
  useEffect(() => {
    if (value !== prevValue) {
      setIsValueChanged(true);
      const timer = setTimeout(() => setIsValueChanged(false), 1000);
      setPrevValue(value);
      return () => clearTimeout(timer);
    }
  }, [value, prevValue]);

  if (!show) return null;

  return (
    <motion.div 
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      className="relative group overflow-hidden min-w-0 flex-1
                bg-gradient-to-br from-[#0f1423] via-[#151b2e] to-[#0f1423]
                backdrop-blur-xl backdrop-saturate-150
                border border-blue-400/20
                shadow-[rgba(0,0,0,0.56)_0px_22px_70px_4px]
                rounded-xl
                hover:border-blue-400/30 transition-colors duration-300"
    >
      <div className="flex items-stretch h-full w-full">
        <motion.div 
          className="bg-[#0f1423] px-4 py-[0.7rem] flex items-center border-r border-blue-400/20 shrink-0"
          whileHover={{ backgroundColor: '#131b2e' }}
          transition={{ duration: 0.2 }}
        >
          <motion.span 
            className="text-[1.8rem] text-[#e5e5e5] font-medium whitespace-nowrap"
            whileHover={{ scale: 1.02 }}
            transition={{ type: "spring", stiffness: 400, damping: 10 }}
          >
            {label}
          </motion.span>
        </motion.div>
        <div className="flex-1 px-4 py-[0.7rem] flex items-center justify-end min-w-0">
          <motion.span 
            className="text-[1.8rem] text-[#e5e5e5] text-right whitespace-nowrap overflow-hidden text-ellipsis"
            animate={isValueChanged ? {
              scale: [1, 1.05, 1],
              color: ['#e5e5e5', '#4ade80', '#e5e5e5'],
              transition: { duration: 0.5 }
            } : {}}
          >
            {value}
          </motion.span>
        </div>
      </div>

      {/* Animated gradient border on hover */}
      <motion.div
        className="absolute inset-0 -z-10 opacity-0 group-hover:opacity-100 transition-opacity duration-300"
        animate={{
          background: [
            'linear-gradient(45deg, rgba(59,130,246,0.1), rgba(59,130,246,0.05), rgba(59,130,246,0.1))',
            'linear-gradient(225deg, rgba(59,130,246,0.1), rgba(59,130,246,0.05), rgba(59,130,246,0.1))'
          ],
        }}
        transition={{
          duration: 4,
          repeat: Infinity,
          repeatType: "reverse"
        }}
      />

      {/* Value change indicator flash */}
      <AnimatePresence>
        {isValueChanged && (
          <motion.div
            initial={{ opacity: 0.5, scale: 0.8 }}
            animate={{ opacity: 0, scale: 1.2 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.5 }}
            className="absolute inset-0 bg-blue-400/20 rounded-xl"
          />
        )}
      </AnimatePresence>
    </motion.div>
  );
};

export default InfoBox;
