import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const TeamScoreBox = ({ teamName, score, overs, logo, isReversed = false }) => {
  const [prevScore, setPrevScore] = useState(score);
  const [isScoreIncreased, setIsScoreIncreased] = useState(false);

  // Format team name if it has 3 or more words
  const formatTeamName = (name) => {
    if (!name) return '';

    const cleanName = name.trim().replace(/\s+/g, ' ');
    const words = cleanName.split(' ').filter(word => word.length > 0);

    if (words.length >= 4) {
      const initials = words
        .map(word => word.charAt(0))
        .join('')
        .toUpperCase();
      return initials;
    }

    return cleanName;
  };

  const displayName = formatTeamName(teamName);

  // Track score changes for animation
  useEffect(() => {
    if (score !== prevScore) {
      setIsScoreIncreased(true);
      const timer = setTimeout(() => setIsScoreIncreased(false), 1000);
      setPrevScore(score);
      return () => clearTimeout(timer);
    }
  }, [score, prevScore]);

  // Animation variants
  const scoreVariants = {
    increased: {
      scale: [1, 1.1, 1],
      color: ['#e5e5e5', '#4ade80', '#e5e5e5'],
      transition: { duration: 0.5 }
    }
  };

  const logoVariants = {
    hover: {
      scale: 1.1,
      filter: 'drop-shadow(0 0 12px rgba(255,255,255,0.5))',
      transition: { duration: 0.3 }
    }
  };

  return (
    <div className={`flex items-center gap-8 ${isReversed ? 'flex-row-reverse' : 'flex-row'}`}>
      {/* Logo Section - Increased size */}
      {logo && (
        <div className="w-28 h-28 relative"> {/* Increased from w-12 h-12 */}
          <motion.img 
            src={logo} 
            alt="" 
            className="w-full h-full object-contain p-2
              filter drop-shadow-[0_0_8px_rgba(255,255,255,0.3)]
              transition-all duration-300 hover:scale-110
              hover:drop-shadow-[0_0_12px_rgba(255,255,255,0.5)]"
            variants={logoVariants}
          />
        </div>
      )}

      <div className={`flex items-center gap-6 min-w-0 ${isReversed ? 'flex-row-reverse' : 'flex-row'}`}>
        {/* Team Name */}
        <div className="flex flex-col items-start justify-center min-w-0">
          <motion.span 
            className="text-8xl font-bold text-[#e5e5e5] leading-none
                     drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]
                     truncate max-w-[400px]" // Added truncate and max-width
            title={teamName}
          >
            {teamName} {/* Use full team name instead of displayName */}
          </motion.span>
        </div>

        {/* Score and Overs */}
        <div className="flex items-center gap-4">
          <motion.span 
            className="text-8xl font-bold text-[#e5e5e5] leading-none relative
                     drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)] whitespace-nowrap"
            animate={isScoreIncreased ? "increased" : ""}
            variants={scoreVariants}
          >
            {score}
          </motion.span>
          {overs && (
            <span className="text-6xl text-blue-300/80 leading-tight
                         drop-shadow-[0_0_4px_rgba(147,197,253,0.3)]
                         relative top-[7px] font-semibold">
              ({overs})
            </span>
          )}
        </div>
      </div>

      {/* Remove the gradient background style */}
    </div>
  );
};

export default TeamScoreBox;
