import React, { useState, useEffect } from 'react';

const SpeedBox = ({ speedData }) => {
  const [currentBall, setCurrentBall] = useState({
    over: '',
    runs: '',
    speed: ''
  });

  useEffect(() => {
    if (speedData?.type === 'speedUpdate' && speedData?.data?.latestBall) {
      const ball = speedData.data.latestBall;
      setCurrentBall({
        over: ball.Over || '',
        runs: ball.Runs || '0',
        speed: ball.Ball_Speed || ''
      });
    } else if (speedData?.length > 0) {
      const latestBall = speedData[0];
      setCurrentBall({
        over: latestBall.Over || '',
        runs: latestBall.Runs || '0',
        speed: latestBall.Ball_Speed || ''
      });
    }
  }, [speedData]);

  return (
    <div className="flex-1 relative min-w-fit
                    bg-gradient-to-br from-[#0f1423] via-[#151b2e] to-[#0f1423]
                    backdrop-blur-xl backdrop-saturate-150
                    border border-blue-400/20
                    shadow-[rgba(0,0,0,0.56)_0px_22px_70px_4px]
                    rounded-xl">
      <div className="flex items-center py-[0.7rem] px-4 relative w-full">
        <div className="flex items-center gap-2">
          <span className="text-2xl font-bold text-[#e5e5e5]">
            {currentBall.over && `${currentBall.over}`}
          </span>
          <span className="text-2xl font-bold text-[#e5e5e5] mx-1">
            {currentBall.runs && `${currentBall.runs}r`}
          </span>
          <span className="text-2xl font-bold text-[#e5e5e5]">
            {currentBall.speed && `${currentBall.speed}`}
          </span>
        </div>
        <div className="ml-auto">
          <span className="text-4xl font-medium text-[#e5e5e5]">
            BALL
          </span>
        </div>
      </div>
    </div>
  );
};

export default SpeedBox;
