import React, { useState, useEffect } from 'react';

const SpeedBox = ({ speedData }) => {
  const [currentBall, setCurrentBall] = useState({
    over: '',
    runs: '',
    speed: ''
  });

  useEffect(() => {
    console.log('SpeedBox received data:', speedData);
    if (speedData?.length > 0) {
      const latestBall = speedData[0];
      console.log('Latest ball:', latestBall);
      setCurrentBall({
        over: latestBall.Over || '',
        runs: latestBall.Runs || '0',
        speed: latestBall.Ball_Speed || ''
      });
    }
  }, [speedData]);

  console.log('Current ball state:', currentBall);

  return (
    <div className="relative rounded-lg p-4 
                    animated-content-box premium-box-shadow content-box-glow
                    border border-blue-400/20
                    transition-all duration-500">
      <h3 className="text-lg font-semibold text-[#e5e5e5] leading-none
                     drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)] mb-2">
        Ball Info
      </h3>
      <div className="flex items-center gap-4">
        <span className="text-xl font-bold text-[#e5e5e5]">
          {currentBall.over ? `Over: ${currentBall.over}` : ''}
        </span>
        <span className="text-xl font-bold text-[#e5e5e5]">
          {currentBall.runs ? `Runs: ${currentBall.runs}` : ''}
        </span>
        <span className="text-xl font-bold text-[#e5e5e5]">
          {currentBall.speed ? `Speed: ${currentBall.speed}` : ''}
        </span>
      </div>
    </div>
  );
};

export default SpeedBox;
