import React from 'react';
import { BackgroundGradientAnimation } from '../../ui/background-gradient-animation';

const ScoreboardRow = ({ children, className }) => {
  return (
    <BackgroundGradientAnimation
      gradientBackgroundStart="rgb(26, 54, 93)"    // Darker blue
      gradientBackgroundEnd="rgb(30, 64, 175)"     // Base blue
      firstColor="37, 99, 235"                     // Bright blue
      secondColor="59, 130, 246"                   // Light blue
      thirdColor="29, 78, 216"                     // Medium blue
      fourthColor="30, 58, 138"                    // Dark blue
      fifthColor="37, 99, 235"                     // Bright blue again
      pointerColor="59, 130, 246"                  // Interactive pointer color
      size="100%"
      blendingValue="soft-light"
      className={className}
      containerClassName="w-full"
      interactive={true}
    >
      <div className="p-4">
        <div className="w-full flex items-center justify-between px-8 py-4
                    animated-content-box premium-box-shadow content-box-glow
                    rounded-lg border border-blue-400/20">
          {children}
        </div>
      </div>
    </BackgroundGradientAnimation>
  );
};

export default ScoreboardRow;
