import React, { useState, useEffect } from 'react';

const InfoBox = ({ label, value, show = true }) => {
  const [animationProgress, setAnimationProgress] = useState(0);
  const [isValueChanged, setIsValueChanged] = useState(false);

  // Track value changes to trigger animations
  useEffect(() => {
    setIsValueChanged(true);
    setAnimationProgress(0);

    const progressTimer = setInterval(() => {
      setAnimationProgress(prev => {
        if (prev >= 100) {
          clearInterval(progressTimer);
          return 100;
        }
        return prev + 5;
      });
    }, 20);

    const changeTimer = setTimeout(() => {
      setIsValueChanged(false);
    }, 2000);

    return () => {
      clearInterval(progressTimer);
      clearTimeout(changeTimer);
    };
  }, [value]);

  if (!show) return null;

  return (
    <div className="relative group overflow-hidden min-w-0 flex-1
                    bg-gradient-to-br from-[#0f1423] via-[#151b2e] to-[#0f1423]
                    backdrop-blur-xl backdrop-saturate-150
                    border border-blue-400/20
                    shadow-[rgba(0,0,0,0.56)_0px_22px_70px_4px]
                    rounded-xl">
      {/* Label Section with Rotating Border */}
      <div className="flex items-stretch h-full w-full relative">
        <div className="relative bg-[#0f1423] px-4 py-[0.7rem] flex items-center border-r border-blue-400/20 shrink-0">
          {/* Animated border */}
          <div className="absolute inset-0 rounded-l-xl">
            <div className="absolute inset-0 rounded-l-xl border-2 border-blue-500/30 
                          animate-[spin_3s_linear_infinite]" />
            <div className="absolute inset-0 rounded-l-xl border-2 border-blue-400/20
                          animate-[ping_2s_cubic-bezier(0,0,0.2,1)_infinite]" />
          </div>

          <span className="text-[1.8rem] text-[#e5e5e5] font-medium whitespace-nowrap relative">
            {label}
          </span>
        </div>

        {/* Value Section with Glow Effect */}
        <div className="flex-1 px-4 py-[0.7rem] flex items-center justify-end min-w-0 relative">
          {/* Background glow */}
          <div 
            className="absolute inset-0 blur-xl opacity-30"
            style={{ 
              background: 'radial-gradient(circle, #60A5FA 0%, transparent 70%)',
              animation: 'pulse 2s infinite'
            }}
          />

          {/* Animated value */}
          <span 
            className={`text-[1.8rem] text-[#e5e5e5] text-right whitespace-nowrap 
                       overflow-hidden text-ellipsis relative
                       ${isValueChanged ? 'animate-speedPulse' : ''}`}
            style={{
              textShadow: isValueChanged ? '0 0 10px #60A5FA' : 'none',
            }}
          >
            {value}
          </span>
        </div>
      </div>

      {/* Progress Bar */}
      <div className="absolute left-0 bottom-0 w-full h-1">
        <div 
          className="h-full bg-blue-500"
          style={{
            width: `${animationProgress}%`,
            transition: 'all 0.3s ease-out',
            boxShadow: '0 0 10px #60A5FA'
          }}
        />
      </div>

      {/* Animations */}
      <style jsx>{`
        @keyframes speedPulse {
          0% { transform: scale(1); }
          50% { transform: scale(1.05); }
          100% { transform: scale(1); }
        }

        @keyframes pulse {
          0% { opacity: 0.2; }
          50% { opacity: 0.4; }
          100% { opacity: 0.2; }
        }
      `}</style>
    </div>
  );
};

export default InfoBox;
