import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { cn } from "../../../utils/cn";

const HoverBorderGradient = ({
  children,
  containerClassName,
  className,
  as: Tag = "div",
  duration = 2,
  clockwise = true,
  ...props
}) => {
  const [hovered, setHovered] = useState(false);
  const [direction, setDirection] = useState("TOP");

  const rotateDirection = (currentDirection) => {
    const directions = ["TOP", "RIGHT", "BOTTOM", "LEFT"];
    const currentIndex = directions.indexOf(currentDirection);
    const nextIndex = clockwise
      ? (currentIndex + 1) % directions.length
      : (currentIndex - 1 + directions.length) % directions.length;
    return directions[nextIndex];
  };

  const movingMap = {
    TOP: "linear-gradient(to bottom, rgba(50, 117, 248, 0.8) 0%, transparent 100%)",
    RIGHT: "linear-gradient(to left, rgba(50, 117, 248, 0.8) 0%, transparent 100%)",
    BOTTOM: "linear-gradient(to top, rgba(50, 117, 248, 0.8) 0%, transparent 100%)",
    LEFT: "linear-gradient(to right, rgba(50, 117, 248, 0.8) 0%, transparent 100%)",
  };

  const highlight = `
    linear-gradient(
      60deg,
      rgba(50, 117, 248, 0.1) 0%,
      rgba(50, 117, 248, 0.3) 25%,
      rgba(50, 117, 248, 0.7) 50%,
      rgba(50, 117, 248, 0.3) 75%,
      rgba(50, 117, 248, 0.1) 100%
    )
  `;

  useEffect(() => {
    const interval = setInterval(() => {
      if (!hovered) {
        setDirection((prevState) => rotateDirection(prevState));
      }
    }, duration * 1000);
    return () => clearInterval(interval);
  }, [hovered, duration, clockwise]);

  return (
    <Tag
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      className={cn(
        "relative flex rounded-xl border-0 content-center bg-transparent items-center flex-col flex-nowrap h-min justify-center overflow-visible p-[2px]",
        containerClassName
      )}
      {...props}
    >
      {/* Animated border gradient */}
      <motion.div
        className="absolute inset-0 rounded-xl overflow-hidden z-0"
        initial={false}
        animate={{
          background: [
            movingMap[direction],
            movingMap[rotateDirection(direction)],
            movingMap[rotateDirection(rotateDirection(direction))],
            movingMap[rotateDirection(rotateDirection(rotateDirection(direction)))],
          ],
        }}
        transition={{
          duration: duration * 4,
          ease: "linear",
          repeat: Infinity,
        }}
        style={{
          filter: "blur(8px)",
        }}
      />

      {/* Hover effect */}
      <motion.div
        className="absolute inset-0 rounded-xl overflow-hidden z-1"
        initial={{ opacity: 0 }}
        animate={{ opacity: hovered ? 1 : 0 }}
        transition={{ duration: 0.3 }}
        style={{
          background: highlight,
          filter: "blur(4px)",
        }}
      />

      {/* Content container */}
      <div 
        className={cn(
          "relative w-full rounded-xl z-10 bg-gradient-to-br from-[#0f1423] to-[#151b2e] backdrop-blur-xl",
          className
        )}
      >
        {children}
      </div>
    </Tag>
  );
};

export default HoverBorderGradient;
