import { cn } from "../../lib/utils";
import React from "react";
import { motion } from "framer-motion";
 
export const BackgroundGradient = ({
  children,
  className,
  containerClassName,
  animate = true,
  gradientColors = {
    start: "#3b82f6",
    middle: "#60a5fa",
    end: "#93c5fd",
    base: "#1d4ed8"
  }
}) => {
  const variants = {
    initial: {
      backgroundPosition: "0 50%",
    },
    animate: {
      backgroundPosition: ["0, 50%", "100% 50%", "0 50%"],
    },
  };

  const gradientStyle = `radial-gradient(circle farthest-side at 0 100%,${gradientColors.start},transparent),
                        radial-gradient(circle farthest-side at 100% 0,${gradientColors.middle},transparent),
                        radial-gradient(circle farthest-side at 100% 100%,${gradientColors.end},transparent),
                        radial-gradient(circle farthest-side at 0 0,${gradientColors.base},#141316)`;

  return (
    <div className={cn("relative p-[4px] group", containerClassName)}>
      <motion.div
        variants={animate ? variants : undefined}
        initial={animate ? "initial" : undefined}
        animate={animate ? "animate" : undefined}
        transition={
          animate
            ? {
                duration: 5,
                repeat: Infinity,
                repeatType: "reverse",
              }
            : undefined
        }
        style={{
          backgroundSize: animate ? "400% 400%" : undefined,
          background: gradientStyle
        }}
        className={cn(
          "absolute inset-0 rounded-xl z-[1] opacity-60 group-hover:opacity-100 blur-xl transition duration-500"
        )}
      />
      <motion.div
        variants={animate ? variants : undefined}
        initial={animate ? "initial" : undefined}
        animate={animate ? "animate" : undefined}
        transition={
          animate
            ? {
                duration: 5,
                repeat: Infinity,
                repeatType: "reverse",
              }
            : undefined
        }
        style={{
          backgroundSize: animate ? "400% 400%" : undefined,
          background: gradientStyle
        }}
        className={cn(
          "absolute inset-0 rounded-xl z-[1]"
        )}
      />
      <div className={cn("relative z-10", className)}>{children}</div>
    </div>
  );
};
