import React, { useEffect, useRef } from 'react';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';

// Utility function to create dots
const createDots = (ctx, width, height, spacing = 30) => {
  ctx.clearRect(0, 0, width, height);

  // Create gradient for dots
  const gradient = ctx.createLinearGradient(0, 0, width, height);
  gradient.addColorStop(0, 'rgba(59, 130, 246, 0.15)'); // Blue-400 with low opacity
  gradient.addColorStop(1, 'rgba(147, 197, 253, 0.15)'); // Blue-300 with low opacity

  for (let x = spacing; x < width - spacing; x += spacing) {
    for (let y = spacing; y < height - spacing; y += spacing) {
      ctx.beginPath();
      ctx.arc(x, y, 1.5, 0, Math.PI * 2);
      ctx.fillStyle = gradient;
      ctx.fill();
    }
  }
};

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  const canvasRef = useRef(null);
  const animationRef = useRef(null);
  const dotsRef = useRef({ x: 0, y: 0 });

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let angle = 0;

    // Set canvas size with device pixel ratio for sharp rendering
    const dpr = window.devicePixelRatio || 1;
    const rect = canvas.getBoundingClientRect();

    canvas.width = rect.width * dpr;
    canvas.height = rect.height * dpr;
    ctx.scale(dpr, dpr);

    // Animation function
    const animate = () => {
      angle += 0.005;
      dotsRef.current = {
        x: Math.cos(angle) * 5,
        y: Math.sin(angle) * 5
      };

      ctx.save();
      ctx.translate(dotsRef.current.x, dotsRef.current.y);
      createDots(ctx, canvas.width, canvas.height);
      ctx.restore();

      animationRef.current = requestAnimationFrame(animate);
    };

    animate();

    // Cleanup
    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, []);

  return (
    <div className="flex flex-col gap-4">
      {/* Animated Canvas Background */}
      <div className="relative w-full">
        <canvas
          ref={canvasRef}
          className="absolute inset-0 w-full h-full opacity-50"
          style={{ 
            filter: 'blur(0.5px)',
            transform: 'translateZ(0)', // Hardware acceleration
          }}
        />

        {/* Main Score Row */}
        <div className="w-full flex items-center justify-between px-8 py-4
                      animated-content-box premium-box-shadow content-box-glow
                      rounded-lg border border-blue-400/20
                      bg-gradient-to-r from-[#0f172a]/95 via-[#1e293b]/95 to-[#0f172a]/95
                      backdrop-blur-sm
                      relative z-10">
          {/* Left Team */}
          <TeamScoreBox 
            teamName={team1.name}
            score={team1.score}
            overs={team1.overs}
            logo={team1.logo}
          />

          {/* Middle Event */}
          <EventBox event={currentEvent} />

          {/* Right Team */}
          <TeamScoreBox 
            teamName={team2.name}
            score={team2.score}
            overs={team2.overs}
            logo={team2.logo}
            isReversed={true}
          />
        </div>
      </div>

      {/* Additional Info Box - Centered below */}
      {additionalInfo && (
        <div className="flex justify-center">
          <AdditionalInfoBox additionalInfo={additionalInfo} />
        </div>
      )}

      {/* Add styles for animations and effects */}
      <style jsx>{`
        .animated-content-box {
          transition: all 0.3s ease-in-out;
          position: relative;
          overflow: hidden;
        }

        .animated-content-box:hover {
          transform: translateY(-2px);
          box-shadow: 0 8px 30px rgba(0, 0, 0, 0.12);
        }

        .animated-content-box::before {
          content: '';
          position: absolute;
          top: 0;
          left: -100%;
          width: 100%;
          height: 100%;
          background: linear-gradient(
            90deg,
            transparent,
            rgba(255, 255, 255, 0.05),
            transparent
          );
          transition: 0.5s;
        }

        .animated-content-box:hover::before {
          left: 100%;
        }

        .content-box-glow {
          box-shadow: 0 0 15px rgba(59, 130, 246, 0.1);
          transition: box-shadow 0.3s ease-in-out;
        }

        .content-box-glow:hover {
          box-shadow: 0 0 25px rgba(59, 130, 246, 0.2);
        }

        @keyframes float {
          0% { transform: translateY(0px); }
          50% { transform: translateY(-5px); }
          100% { transform: translateY(0px); }
        }

        .premium-box-shadow {
          box-shadow: 
            rgba(0, 0, 0, 0.17) 0px -23px 25px 0px inset,
            rgba(0, 0, 0, 0.15) 0px -36px 30px 0px inset,
            rgba(0, 0, 0, 0.1) 0px -79px 40px 0px inset,
            rgba(0, 0, 0, 0.06) 0px 2px 1px;
        }
      `}</style>
    </div>
  );
};

export default ScoreboardRow;
