import React, { useState, useEffect } from 'react';

const SpeedBox = ({ speedData }) => {
  const [currentBall, setCurrentBall] = useState({
    runs: '0'
  });
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    if (!speedData || (Array.isArray(speedData) && speedData.length === 0)) {
      return;
    }

    if (Array.isArray(speedData)) {
      const latestBall = speedData[0];
      if (latestBall) {
        setCurrentBall({
          runs: latestBall.Runs || '0'
        });
        setIsVisible(true);
        const timer = setTimeout(() => {
          setIsVisible(false);
        }, 5000);
        return () => clearTimeout(timer);
      }
    } 
    else if (typeof speedData === 'object') {
      setCurrentBall({
        runs: speedData.Runs || '0'
      });
      setIsVisible(true);
      const timer = setTimeout(() => {
        setIsVisible(false);
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [speedData]);

  return (
    <div className={`absolute top-0 right-0 z-10
                    bg-gradient-to-br from-[#0f1423] via-[#151b2e] to-[#0f1423]
                    backdrop-blur-xl backdrop-saturate-150
                    border border-blue-400/20
                    shadow-[rgba(0,0,0,0.56)_0px_22px_70px_4px]
                    rounded-xl p-2
                    transition-all duration-500 ease-in-out
                    ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 -translate-y-2'}`}>
      <span className="text-[1.2rem] leading-[1rem] font-normal text-[#e5e5e5]">
        {currentBall.runs}r
      </span>
    </div>
  );
};

export default SpeedBox;
