import { cn } from "../../lib/utils";
import React from "react";

export const Meteors = ({
  number,
  className,
}) => {
  const meteors = new Array(number || 20).fill(true);
  return (
    <>
      {meteors.map((el, idx) => (
        <span
          key={"meteor" + idx}
          className={cn(
            "absolute h-1 w-1 rounded-full rotate-[215deg] animate-meteor-effect",
            "before:content-[''] before:absolute before:top-1/2 before:-translate-y-[50%]",
            "before:w-[100px] before:h-[2px] before:bg-gradient-to-r before:from-[#3b82f6] before:to-transparent",
            "will-change-transform",
            className
          )}
          style={{
            top: Math.floor(Math.random() * -20) + "%",
            left: Math.floor(Math.random() * 100) + "%",
            animationDelay: Math.random() * 3 + "s",
            animationDuration: Math.floor(Math.random() * (8 - 4) + 4) + "s",
          }}
        >
          <div className="absolute h-full w-full rounded-full bg-blue-500 blur-[2px]" />
        </span>
      ))}
    </>
  );
};
