import React, { useState, useEffect } from 'react';

const SpeedBox = ({ speedData }) => {
  const [speeds, setSpeeds] = useState([]);

  useEffect(() => {
    if (speedData?.length > 0) {
      // Get the first over's speeds (most recent)
      const currentOverSpeeds = speedData[0]?.Ball_Speed_This_Over || '';
      // Convert the string of speeds into an array and remove empty/quote values
      const speedArray = currentOverSpeeds
        .split(',')
        .map(speed => speed.replace(/"/g, ''))
        .filter(speed => speed !== '');
      setSpeeds(speedArray);
    }
  }, [speedData]);

  return (
    <div className="relative rounded-lg p-4 
                    animated-content-box premium-box-shadow content-box-glow
                    border border-blue-400/20
                    transition-all duration-500">
      <h3 className="text-lg font-semibold text-[#e5e5e5] leading-none
                     drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)] mb-2">
        Ball Speed
      </h3>
      <div className="flex flex-wrap gap-2 justify-start">
        {speeds.map((speed, index) => (
          <span
            key={index}
            className="w-10 h-10 flex-shrink-0 flex items-center justify-center 
                     rounded-full text-sm font-bold
                     bg-gradient-to-br from-indigo-500 to-indigo-600
                     text-[#e5e5e5] leading-none
                     drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]
                     transition-all duration-300"
          >
            {speed ? `${speed}` : '•'}
          </span>
        ))}
        {!speeds.length && (
          <span className="w-10 h-10 flex-shrink-0 flex items-center justify-center 
                        rounded-full text-xl font-bold
                        bg-gradient-to-br from-gray-600 to-gray-700
                        text-[#e5e5e5] leading-none
                        drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]
                        transition-all duration-300">
            •
          </span>
        )}
      </div>
    </div>
  );
};

export default SpeedBox;
