import React from 'react';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';
import { motion } from 'framer-motion';

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  return (
    <div className="flex flex-col gap-4">
      {/* Main Score Row */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full flex items-center justify-between px-8 py-4
                  animated-content-box premium-box-shadow content-box-glow
                  rounded-lg border border-blue-400/20"
      >
        {/* Left Team */}
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.2, duration: 0.5 }}
        >
          <TeamScoreBox 
            teamName={team1.name}
            score={team1.score}
            overs={team1.overs}
            logo={team1.logo}
          />
        </motion.div>

        {/* Middle Event */}
        <motion.div
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.3, duration: 0.5 }}
        >
          <EventBox event={currentEvent} />
        </motion.div>

        {/* Right Team */}
        <motion.div
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.2, duration: 0.5 }}
        >
          <TeamScoreBox 
            teamName={team2.name}
            score={team2.score}
            overs={team2.overs}
            logo={team2.logo}
            isReversed={true}
          />
        </motion.div>
      </motion.div>

      {/* Additional Info Box - Centered below */}
      {additionalInfo && (
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4, duration: 0.5 }}
          className="flex justify-center"
        >
          <AdditionalInfoBox additionalInfo={additionalInfo} />
        </motion.div>
      )}
    </div>
  );
};

export default ScoreboardRow;
