import React, { useState, useEffect } from 'react';

const SpeedBox = ({ speedData }) => {
  console.log('SpeedBox received data:', speedData);
  const [currentBall, setCurrentBall] = useState(null);

  useEffect(() => {
    if (speedData?.length > 0) {
      // Get the most recent ball data
      const latestBall = speedData[0];
      if (latestBall) {
        setCurrentBall({
          over: latestBall.Over,
          runs: latestBall.Runs,
          speed: latestBall.Ball_Speed
        });
      }
      console.log('Current ball data:', latestBall);
    }
  }, [speedData]);

  return (
    <div className="relative rounded-lg p-4 
                    animated-content-box premium-box-shadow content-box-glow
                    border border-blue-400/20
                    transition-all duration-500">
      <h3 className="text-lg font-semibold text-[#e5e5e5] leading-none
                     drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)] mb-2">
        Ball Speed
      </h3>
      <div className="flex items-center justify-start">
        {currentBall ? (
          <span className="text-xl font-bold text-[#e5e5e5] leading-none">
            Over: {currentBall.over}, Runs: {currentBall.runs}, Speed: {currentBall.speed}
          </span>
        ) : (
          <span className="text-xl font-bold text-[#e5e5e5] leading-none">
            Waiting for ball data...
          </span>
        )}
      </div>
    </div>
  );
};

export default SpeedBox;
