import React, { useState, useEffect, useRef } from 'react';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';
import { motion, AnimatePresence } from 'framer-motion';

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  // Stars configuration
  const stars = 108;
  const columns = 18;
  const [glowingStars, setGlowingStars] = useState([]);
  const [mouseEnter, setMouseEnter] = useState(false);
  const highlightedStars = useRef([]);

  useEffect(() => {
    const interval = setInterval(() => {
      highlightedStars.current = Array.from({ length: 5 }, () =>
        Math.floor(Math.random() * stars)
      );
      setGlowingStars([...highlightedStars.current]);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const Star = ({ isGlowing, delay }) => (
    <motion.div
      key={delay}
      initial={{ scale: 1 }}
      animate={{
        scale: isGlowing ? [1, 1.2, 2.5, 2.2, 1.5] : 1,
        background: isGlowing ? "#fff" : "#666",
      }}
      transition={{
        duration: 2,
        ease: "easeInOut",
        delay: delay,
      }}
      className="bg-[#666] h-[1px] w-[1px] rounded-full relative z-[1]"
    />
  );

  const Glow = ({ delay }) => (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{
        duration: 2,
        ease: "easeInOut",
        delay: delay,
      }}
      exit={{ opacity: 0 }}
      className="absolute left-1/2 -translate-x-1/2 z-[1] h-[4px] w-[4px] rounded-full bg-blue-500 blur-[1px] shadow-2xl shadow-blue-400"
    />
  );

  return (
    <div className="flex flex-col gap-4">
      {/* Main Score Row */}
      <div className="relative w-full"
           onMouseEnter={() => setMouseEnter(true)}
           onMouseLeave={() => setMouseEnter(false)}>
        {/* Stars Background */}
        <div className="absolute inset-0 overflow-hidden rounded-lg z-[1]"
             style={{
               display: "grid",
               gridTemplateColumns: `repeat(${columns}, 1fr)`,
               gap: `1px`,
             }}>
          {[...Array(stars)].map((_, starIdx) => {
            const isGlowing = glowingStars.includes(starIdx);
            const delay = (starIdx % 10) * 0.1;
            const staticDelay = starIdx * 0.01;
            return (
              <div
                key={`star-${starIdx}`}
                className="relative flex items-center justify-center"
              >
                <Star
                  isGlowing={mouseEnter ? true : isGlowing}
                  delay={mouseEnter ? staticDelay : delay}
                />
                {mouseEnter && <Glow delay={staticDelay} />}
                <AnimatePresence mode="wait">
                  {isGlowing && <Glow delay={delay} />}
                </AnimatePresence>
              </div>
            );
          })}
        </div>

        {/* Existing Content */}
        <div className="relative z-[2] flex items-center justify-between px-8 py-4
                    animated-content-box premium-box-shadow content-box-glow
                    rounded-lg border border-blue-400/20">
          {/* Left Team */}
          <TeamScoreBox 
            teamName={team1.name}
            score={team1.score}
            overs={team1.overs}
            logo={team1.logo}
          />

          {/* Middle Event */}
          <EventBox event={currentEvent} />

          {/* Right Team */}
          <TeamScoreBox 
            teamName={team2.name}
            score={team2.score}
            overs={team2.overs}
            logo={team2.logo}
            isReversed={true}
          />
        </div>
      </div>

      {/* Additional Info Box - Centered below */}
      {additionalInfo && (
        <div className="flex justify-center">
          <AdditionalInfoBox additionalInfo={additionalInfo} />
        </div>
      )}
    </div>
  );
};

export default ScoreboardRow;
