import React from 'react';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';
import { motion } from 'framer-motion';
import { BackgroundGradientAnimation } from '../../../components/ui/background-gradient-animation';

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  return (
    <div className="flex flex-col gap-4">
      <BackgroundGradientAnimation
        gradientBackgroundStart="rgb(13, 22, 49)"
        gradientBackgroundEnd="rgb(26, 36, 66)"
        firstColor="18, 113, 255"
        secondColor="100, 0, 200"
        thirdColor="0, 40, 100"
        fourthColor="20, 0, 60"
        fifthColor="0, 20, 80"
        pointerColor="80, 100, 255"
        size="120%"
        blendingValue="overlay"
        containerClassName="rounded-xl relative overflow-hidden"
      >
        {/* Main Score Row - Enhanced with animations */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ 
            type: "spring",
            stiffness: 260,
            damping: 20
          }}
          className="w-full grid grid-cols-[auto_1fr_auto] items-center gap-4 px-8 py-4
                     rounded-xl border-2 border-[rgb(28,31,63)] relative z-10
                     bg-gradient-to-br from-[#0f1631]/95 via-[#162448]/95 to-[#0c1229]/95 
                     backdrop-blur-md
                     shadow-[rgba(0,0,0,0.56)_0px_22px_70px_4px,inset_0px_0px_20px_rgba(28,31,63,0.5)]
                     hover:shadow-[rgba(0,0,0,0.7)_0px_30px_90px_4px,inset_0px_0px_30px_rgba(28,31,63,0.6)]
                     transition-all duration-300 ease-in-out
                     hover:scale-[1.02] hover:border-opacity-75
                     [transform-style:preserve-3d]
                     after:content-[''] after:absolute after:inset-0 
                     after:bg-gradient-to-r after:from-transparent 
                     after:via-white/5 after:to-transparent 
                     after:opacity-0 after:hover:opacity-100 
                     after:transition-opacity after:duration-500"
        >
          {/* Left Team with stagger animation */}
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.2 }}
          >
            <TeamScoreBox 
              teamName={team1.name}
              score={team1.score}
              overs={team1.overs}
              logo={team1.logo}
            />
          </motion.div>

          {/* Middle Event with fade animation */}
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
          >
            <EventBox event={currentEvent} />
          </motion.div>

          {/* Right Team with stagger animation */}
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.2 }}
          >
            <TeamScoreBox 
              teamName={team2.name}
              score={team2.score}
              overs={team2.overs}
              logo={team2.logo}
              isReversed={true}
            />
          </motion.div>
        </motion.div>
      </BackgroundGradientAnimation>

      {/* Additional Info Box with fade up animation */}
      {additionalInfo && (
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
          className="flex justify-center"
        >
          <AdditionalInfoBox additionalInfo={additionalInfo} />
        </motion.div>
      )}
    </div>
  );
};

export default ScoreboardRow;
