import React from 'react';

const AdditionalInfoBox = ({ additionalInfo }) => {
  if (!additionalInfo) return null;

  return (
    <div className="relative px-6 py-2 rounded-lg overflow-hidden
                    border border-blue-400/20 
                    shadow-[0_0_15px_rgba(59,130,246,0.15)]
                    backdrop-blur-lg
                    transition-all duration-300
                    hover:shadow-[0_0_20px_rgba(59,130,246,0.25)]
                    group">
      {/* Background glow effect */}
      <div 
        className="absolute inset-0 blur-xl opacity-30"
        style={{ 
          background: 'radial-gradient(circle, rgba(59,130,246,0.3) 0%, transparent 70%)',
          animation: 'pulse 2s infinite'
        }}
      />

      {/* Animated border glow */}
      <div className="absolute inset-0 rounded-lg opacity-50"
           style={{
             animation: 'borderGlow 2s infinite',
             border: '1px solid rgba(59,130,246,0.2)',
           }} />

      {/* Content with glow */}
      <span className="relative z-10 text-2xl font-semibold text-center leading-tight whitespace-nowrap
                     text-[#e5e5e5] leading-none
                     drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]"
            style={{
              textShadow: '0 0 10px rgba(59,130,246,0.3)',
              animation: 'textPulse 2s infinite'
            }}>
        {additionalInfo}
      </span>

      {/* Shine effect */}
      <div className="absolute inset-0 z-20 
                    bg-gradient-to-r from-transparent via-white/10 to-transparent 
                    translate-x-[-200%] group-hover:translate-x-[200%] 
                    transition-transform duration-1000 ease-in-out" />

      <style jsx>{`
        @keyframes pulse {
          0% { opacity: 0.2; }
          50% { opacity: 0.4; }
          100% { opacity: 0.2; }
        }

        @keyframes borderGlow {
          0% { box-shadow: 0 0 5px rgba(59,130,246,0.2); }
          50% { box-shadow: 0 0 15px rgba(59,130,246,0.4); }
          100% { box-shadow: 0 0 5px rgba(59,130,246,0.2); }
        }

        @keyframes textPulse {
          0% { opacity: 0.8; }
          50% { opacity: 1; }
          100% { opacity: 0.8; }
        }
      `}</style>
    </div>
  );
};

export default AdditionalInfoBox;
